        ,[PSCustomObject]@{ Id = 'f011adf6-475a-48c9-bf26-8db051cb6964'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Data Explorer'; ResourceType = 'microsoft.kusto/clusters'; Key = 'UpdateCachePoliciesForAdxTables'; Message = 'Query time range is too wide - Consider setting your cache period (policy) to the recommended value'; State = 'Disabled'; Version = 5.5; Description = 'Review table cache policies for Data Explorer tables'; LongDescription = 'This recommendation surfaces Data Explorer tables with a high number of queries that look back beyond the configured cache period (policy). (You''ll see the top 10 tables by query percentage that access out-of-cache data). The recommended action to improve the performance: Limit queries on this table to the minimal necessary time range (within the defined policy). Alternatively, if data from the entire time range is required, increase the cache period to the recommended value.'; Benefits = 'Optimize performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/adxcachepolicy'; }
