        ,[PSCustomObject]@{ Id = '900b17ec-0125-458a-a065-6c213e57c342'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Data Explorer'; ResourceType = 'microsoft.kusto/clusters'; Key = 'RunCleanupCommandForAzureDataExplorer'; Message = 'Run the cleanup command to detect and delete unused storage in Data Explorer resources'; State = 'Active'; Version = 2.8; Description = 'Cleanup unused storage in Data Explorer resources'; LongDescription = 'Over time, internal extents merge operations can accumulate redundant and unused storage artifacts that remain beyond the data retention period. While this unreferenced data doesn''t negatively impact the performance, it can lead to more storage use and larger costs than necessary. This recommendation surfaces Data Explorer resources that have unused storage artifacts. The recommended action is to run the cleanup command to detect and delete unused storage artifacts and reduce cost. Note that data recoverability will be reset to the cleanup time and will not be available on data that was created before running the cleanup.'; Benefits = 'Optimize cost'; Tip = ''; LearnMoreLink = 'https://aka.ms/adxcleanextentcontainers'; }
