        ,[PSCustomObject]@{ Id = '9a3ea211-a282-4ab6-a63b-81024975b796'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Data Explorer'; ResourceType = 'microsoft.kusto/clusters'; Key = 'ReduceCacheForAzureDataExplorerTablesOperational Excellence'; Message = 'Reduce the cache in the cache policy'; State = 'Active'; Version = 3.1; Description = 'Reduce the cache policy on your Data Explorer tables'; LongDescription = 'Based on your actual usage during the last month, update the cache policy to reduce the hot cache for the table. The number of instances in your cluster is determined by the CPU and ingestion load, not by the amount of data held in the hot cache and may change based on your usage. Based on current usage, changing the cache isn''t enough to reduce the number of instances, we recommend further optimizations,such as changing the SKU, reducing the CPU load, and enabling autoscale to scale in efficiently. '; Benefits = 'Cache reduction'; Tip = ''; LearnMoreLink = 'https://aka.ms/adxcachepolicy'; }
        ,[PSCustomObject]@{ Id = '389653ce-d564-4b95-aac4-ca30e1602536'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Data Explorer'; ResourceType = 'microsoft.kusto/clusters'; Key = 'IncreaseCacheForAzureDataExplorerTablesToImprovePerformance'; Message = 'Increase the cache in the cache policy'; State = 'Active'; Version = 3.3; Description = 'Increase the cache in the cache policy'; LongDescription = 'Based on your actual usage during the last month, update the cache policy to increase the hot cache for the table. Note that the retention period must always be larger than the cache period. If, after increasing the cache, the retention period is lower than the cache period, please update the retention policy.(*) The analysis is based only on user queries that scanned data. '; Benefits = 'Optimize performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/adxcachepolicy'; }
