        ,[PSCustomObject]@{ Id = 'e387838a-4fbc-47d5-9a3d-9d1aaa218345'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Redis Cache'; ResourceType = 'microsoft.cache/redis'; Key = 'Persistence'; Message = 'Enable Persistence'; State = 'Active'; Version = 1; Description = 'Redis persistence allows you to persist data stored in a cache so you can reload data from an event that caused data loss.'; LongDescription = 'Redis persistence allows you to persist data stored in Redis. You can also take snapshots and back up the data. If there''s a hardware failure, the persisted data is automatically loaded in your cache instance.  Data loss is possible if a failure occurs where Cache nodes are down.'; Benefits = 'Avoid data loss due to hardware failure or Cache node failure'; Tip = 'Enable Persistence to avoid data loss in case of hardware or node failure.'; LearnMoreLink = 'https://aka.ms/redis/persistence'; }
        ,[PSCustomObject]@{ Id = '204cc04b-0e75-46f9-9a43-9bcb39955236'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Redis Cache'; ResourceType = 'microsoft.cache/redis'; Key = 'MigrateFromCloudService'; Message = 'Migrate from Cloud Service'; State = 'Active'; Version = 1; Description = 'Cloud service caches are being retired in August 2024, migrate before then to avoid any problems'; LongDescription = 'This instance of Azure Cache for Redis has a dependency on Cloud Services (classic) which is being retired in August 2024. Please follow the instructions found in the learn more link to migrate to an instance without this dependency. If you need to upgrade your cache to Redis 6 please note that upgrading a cache with a dependency on cloud services is not supported. You should migrate your cache instance to Virtual Machine Scale Set before upgrading. For more information, see https://learn.microsoft.com/azure/azure-cache-for-redis/cache-faq for details on cloud services hosted caches. Note: If you have completed your migration away from Cloud Services, please allow up to 24 hours for this recommendation to be removed'; Benefits = 'Avoid service interruptions by migrating before cloud services are retired.'; Tip = 'Migrate from Cloud Service before the end of support.'; LearnMoreLink = 'https://docs.microsoft.com/azure/azure-cache-for-redis/cache-faq#caches-with-a-dependency-on-cloud-services-%28classic%29'; }
