        ,[PSCustomObject]@{ Id = '459f28d1-3669-43a7-8981-2dcbfa862608'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'SAP on Azure'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'UpdateToLatestWaasApiVersionAtSub'; Message = 'Upgrade to the latest Workloads API version'; State = 'Active'; Version = 2; Description = 'Upgrade Workloads API to the latest version (Azure Center for SAP solutions API)'; LongDescription = 'We have identified calls to an outdated Workloads API version for resources under this resource group. We recommend switching to the latest Workloads API version to ensure uninterrupted access to latest features and performance improvements in Azure Center for SAP solutions. If there are multiple Virtual Instances for SAP solutions (VIS) shown in the recommendation, please ensure you update the API version for all VIS resources.'; Benefits = 'Latest Workloads API contain fixes for known issues and additional improvements'; Tip = 'Upgrade to the latest Workloads API version'; LearnMoreLink = 'https://go.microsoft.com/fwlink/?linkid=2228001'; }
        ,[PSCustomObject]@{ Id = 'f5a05237-91e2-4ecf-8394-f020c33665a1'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'SAP on Azure'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'UpgradeToLatestWaasSdkVersionAtSub'; Message = 'Upgrade to the latest Workloads SDK version for Azure Center for SAP solutions'; State = 'Active'; Version = 2; Description = 'Upgrade Workloads SDK to the latest version (Azure Center for SAP solutions SDK)'; LongDescription = 'We have identified calls to an outdated Workloads SDK version from resources in this Resource Group. Please upgrade to the latest Workloads SDK version to get the latest features and the best results in terms of model quality, performance and service availability for Azure Center for SAP solutions. If there are multiple Virtual Instances for SAP solutions (VIS) shown in the recommendation, please ensure you update the SDK version for all VIS resources.'; Benefits = 'Latest Workloads SDK contains fixes for known issues and additional improvements.'; Tip = ''; LearnMoreLink = 'https://go.microsoft.com/fwlink/?linkid=2228000'; }
        ,[PSCustomObject]@{ Id = '93214083-e48e-420e-881f-89d02265f831'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Migrate'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'UpdateToLatestSapMigrateDiscoveryApiVersionAtSub'; Message = 'Upgrade to the latest SAP Migrate Discovery API version'; State = 'Active'; Version = 2; Description = 'Upgrade SAP Migrate Discovery API to the latest version'; LongDescription = 'An outdated version of the SAP Migrate Discovery API has been detected for resources within the SAP Discovery Site under your subscription. To ensure access to the latest features and performance enhancements, we recommend upgrading to the most recent version of the API.'; Benefits = 'Latest API contain fixes for known issues and additional improvements'; Tip = 'Upgrade to the latest SAP Migrate Discovery API version'; LearnMoreLink = 'https://go.microsoft.com/fwlink/?linkid=2263529'; }
