        ,[PSCustomObject]@{ Id = '02cfb5ef-a0c1-4633-9854-031fbda09946'; Category = 'High Availability'; Control = 'High Availability'; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/availabilitysets'; Key = 'ManagedDisksAvSet'; Message = 'Use Managed Disks'; State = 'Active'; Version = 8; Description = 'Improve data reliability by using Managed Disks'; LongDescription = 'Virtual machines in an Availability Set with disks that share either storage accounts or storage scale units are not resilient to single storage scale unit failures during outages. Migrate to Azure Managed Disks to ensure that the disks of different VMs in the Availability Set are sufficiently isolated to avoid a single point of failure.'; Benefits = 'Ensure business continuity through data resilience'; Tip = 'You can use Managed Disks to ensure data resilience.'; LearnMoreLink = 'https://aka.ms/aa_avset_manageddisk_learnmore'; }
        ,[PSCustomObject]@{ Id = '702b474d-698f-4029-9f9d-4782c626923e'; Category = 'Cost'; Control = ''; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/snapshots'; Key = 'ManagedDiskSnapshot'; Message = 'Use Standard snapshots for Managed Disks'; State = 'Active'; Version = 1.2; Description = 'Use Standard Storage to store Managed Disks snapshots'; LongDescription = 'To save 60% of cost, we recommend storing your snapshots in Standard Storage, regardless of the storage type of the parent disk. This option is the default for Managed Disks snapshots. Migrate your snapshot from Premium to Standard Storage. Refer to Managed Disks pricing details.'; Benefits = '60% reduction in the snapshot cost for Managed Disks'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_manageddisksnapshot_learnmore'; }
        ,[PSCustomObject]@{ Id = 'ed651749-cd37-4fd5-9897-01b416926745'; Category = 'High Availability'; Control = 'Disaster Recovery'; Impact = 'Medium'; ServiceName = 'Site Recovery'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'ASRUnprotectedVMs'; Message = 'Enable virtual machine replication'; State = 'Active'; Version = 7; Description = 'Enable virtual machine replication to protect your applications from regional outage'; LongDescription = 'Virtual machines are resilient to regional outages when replication to another region is enabled. To reduce adverse business impact during an Azure region outage, we recommend enabling replication of all business-critical virtual machines.'; Benefits = 'Ensure business continuity in case of any Azure region outage'; Tip = ''; LearnMoreLink = 'https://aka.ms/azure-site-recovery-dr-azure-vms'; }
        ,[PSCustomObject]@{ Id = 'bcfeb92b-fe93-4cea-adc6-e747055518e9'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Site Recovery'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'ASRUpdateOutboundConnectivityProtocolToServiceTags'; Message = 'Update your outbound connectivity protocol to Service Tags for Azure Site Recovery'; State = 'Active'; Version = 9; Description = 'Update your outbound connectivity protocol to Service Tags for Azure Site Recovery'; LongDescription = 'IP address-based allowlisting is a vulnerable way to control outbound connectivity for firewalls, Service Tags are a good  alternative. We highly recommend the use of Service Tags, to allow connectivity to Azure Site Recovery services for the machines.'; Benefits = 'Ensures better security, stability and resiliency than hard coded IP Addresses'; Tip = ''; LearnMoreLink = 'https://aka.ms/azure-site-recovery-using-service-tags'; }
        ,[PSCustomObject]@{ Id = '7cdecd2c-a8b0-41cf-b0a4-528bd4d85f5a'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'AzureStorageVmUltraDisk'; Message = 'Learn more about Ultra disk'; State = 'Active'; Version = 4; Description = 'Take advantage of Ultra Disk low latency for your log disks and improve your database workload performance.'; LongDescription = 'Ultra disk is available in the same region as your database workload. Ultra disk offers high throughput, high IOPS, and consistent low latency disk storage for your database workloads: For Oracle DBs, you can now use either 4k or 512E sector sizes with Ultra disk depending on your Oracle DB version. For SQL server, leveraging Ultra disk for your log disk might offer more performance for your database. See instructions here for migrating your log disk to Ultra disk.'; Benefits = 'Boost the performance of your database on IaaS VMs by using Ultra disk as log disk.'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/disks-enable-ultra-ssd?tabs=azure-portal'; }
        ,[PSCustomObject]@{ Id = '9b0d1cf7-8a3a-4c8b-8f9f-1c3e70e399d6'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'MatchProdVMProdDisks'; Message = 'Match production Virtual Machines with Production Disk for consistent performance and better latency'; State = 'Active'; Version = 1.2; Description = 'Match production Virtual Machines with Production Disk for consistent performance and better latency'; LongDescription = 'Production virtual machines need production disks if you want to get the best performance. We see that you are running a production level virtual machine, however, you are using a low performing disk with standard HDD. Upgrading your disks that are attached to your production disks, either Standard SSD or Premium SSD, will benefit you with a more consistent experience and improvements in latency.'; Benefits = 'More consistent performance, better latency'; Tip = 'Match Production Virtual Machine with Production Disks.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/windows/disks-types#disk-comparison'; }
        ,[PSCustomObject]@{ Id = '58d6648d-32e8-4346-827c-4f288dd8ca24'; Category = 'High Availability'; Control = 'High Availability'; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'MigrateStandardStorageAccountToPremium'; Message = 'Migrate Standard disks to Premium disks'; State = 'Disabled'; Version = 8; Description = 'Upgrade the standard disks attached to your premium-capable VM to premium disks'; LongDescription = 'Using Standard SSD disks with premium VMs may lead to suboptimal performance and latency issues. We recommend that you consider upgrading the standard disks to premium disks. For any Single Instance Virtual Machine using premium storage for all Operating System Disks and Data Disks, we guarantee Virtual Machine Connectivity of at least 99.9%. When choosing to upgrade, there are two factors to consider. The first factor is that upgrading requires a VM reboot and that takes 3-5 minutes to complete. The second is if the VMs in the list are mission-critical production VMs, evaluate the improved availability against the cost of premium disks.'; Benefits = 'Improved availability with single VM SLA available only when all disks are premium'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_storagestandardtopremium_learnmore'; }
        ,[PSCustomObject]@{ Id = '02c15f98-aedb-481b-884b-af5847b2bf3d'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Virtual Machines'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'SIVM'; Message = 'Use availability sets'; State = 'Disabled'; Version = 6; Description = 'Use availability sets for improved fault tolerance'; LongDescription = 'Group two or more virtual machines in an availability set to ensure at least one virtual machine is available during an outage.'; Benefits = 'Ensure business continuity through virtual machine resilience'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_sivmrec_learnmore'; }
        ,[PSCustomObject]@{ Id = '158f0a07-0a66-4a25-9e37-c43c49c8dd64'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Virtual Machines'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'ComputeStaleSDKRecommendation'; Message = 'Update your current Compute Management SDK version to the most recent version'; State = 'Disabled'; Version = 5; Description = 'Update your current Compute Management SDK version to the most recent version.'; LongDescription = 'We have identified API calls from outdated Compute Management SDKs for resources under this subscription. We recommend switching to the latest Compute Management SDK versions. You need to update your existing code or scripts to use the latest SDK version. This ensures you receive the latest security and performance improvements.'; Benefits = 'Improved security, performance, data resiliency, and support for new features.'; Tip = 'Regularly update your Compute Management SDK versions to prevent security issues and access new features.'; LearnMoreLink = 'https://aka.ms/azureSDKReadMe'; }
        ,[PSCustomObject]@{ Id = '57ecb3cd-f2b4-4cad-8b3a-232cca527a0b'; Category = 'High Availability'; Control = 'High Availability'; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'UpgradeVMToManagedDisksWithoutAdditionalCost'; Message = 'Upgrade VM to Managed Disks at no additional cost'; State = 'Active'; Version = 6; Description = 'Upgrade VM from Premium Unmanaged Disks to Managed Disks at no additional cost'; LongDescription = 'Azure Managed Disks provide higher resiliency, simplified service management, higher scale target and more choices among several disk types. Your VM is using premium unmanaged disks that can be migrated to managed disks at no additional cost through the portal in less than 5 minutes.'; Benefits = 'Leverage higher resiliency and other benefits of Managed Disks'; Tip = ''; LearnMoreLink = 'https://aka.ms/md_overview'; }
        ,[PSCustomObject]@{ Id = 'af0be9f8-e590-48d0-9e19-380d8819b8dc'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Backup'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'VmNoBackup'; Message = 'Enable Virtual Machine Backup'; State = 'Disabled'; Version = 7.1; Description = 'Enable virtual machine backup to protect your data from corruption and accidental deletion'; LongDescription = 'Configure virtual machine backup to protect your mission critical data against accidental deletion or corruption.'; Benefits = 'Improved data resilience and performance'; Tip = 'You can enable virtual machine backup to protect your data from corruption or accidental deletion.'; LearnMoreLink = 'https://aka.ms/aa_vmbackup_learnmore'; }
        ,[PSCustomObject]@{ Id = '11f04d70-5bb3-4065-b717-1f11b2e050a8'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'High'; ServiceName = 'Compute_Images'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'VMRunningDeprecatedImage'; Message = 'Upgrade your VM to alternative image version'; State = 'Active'; Version = 16; Description = 'Upgrade your deprecated Virtual Machine image to a newer image'; LongDescription = 'Virtual Machines (VMs) in your subscription are running on images scheduled for deprecation. Once the image is deprecated, new VMs can''t be created from the deprecated image. To prevent disruption to your workloads, upgrade to a newer image. (VMRunningDeprecatedImage)'; Benefits = 'Minimize any potential disruptions to your VM workloads'; Tip = ''; LearnMoreLink = 'https://aka.ms/DeprecatedImagesFAQ'; }
