        ,[PSCustomObject]@{ Id = '3b739bd1-c193-4bb6-a953-1362ee3b03b2'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'High'; ServiceName = 'Compute_Images'; ResourceType = 'microsoft.compute/virtualmachinescalesets'; Key = 'VMScaleSetRunningDeprecatedImage'; Message = 'Upgrade your VMSS to alternative image version'; State = 'Active'; Version = 10; Description = 'Upgrade your VMSS to alternative image version'; LongDescription = 'VMSS in your subscription are running on images that have been scheduled for deprecation. Once the image is deprecated, your VMSS workloads would no longer scale out. Upgrade to newer version of the image to prevent disruption to your workload.'; Benefits = 'Minimize any potential disruptions to your VMSS workloads'; Tip = ''; LearnMoreLink = 'https://aka.ms/DeprecatedImagesFAQ'; }
