        ,[PSCustomObject]@{ Id = 'A0C6DF20-B77A-4215-A877-A8EE03CEB156'; Category = 'Operational Excellence'; Control = ''; Impact = 'Low'; ServiceName = 'Azure Container Apps'; ResourceType = 'microsoft.app/containerapps'; Key = 'UseLatestApiVersion'; Message = 'The api version you use for Microsoft.App is deprecated, please use latest api version'; State = 'Active'; Version = 1; Description = 'The api version you use for Microsoft.App is deprecated, please use latest api version'; LongDescription = 'The api version you use for Microsoft.App is deprecated, please use latest api version'; Benefits = 'More stable api experience'; Tip = ''; LearnMoreLink = 'http://aka.ms/containerappsapiversion'; }
        ,[PSCustomObject]@{ Id = 'c692e862-953b-49fe-9c51-e5d2792c1cc1'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure Container Apps'; ResourceType = 'microsoft.app/managedenvironments'; Key = 'CreateNewContainerAppsEnvironment'; Message = 'Re-create your your Container Apps environment to avoid DNS issues'; State = 'Active'; Version = 6; Description = 'Re-create your your Container Apps environment to avoid DNS issues'; LongDescription = 'There''s a potential networking issue  with your Container Apps environments that might cause DNS issues. We recommend that you create a new Container Apps environment, re-create your Container Apps in the new environment, and delete the old Container Apps environment.'; Benefits = 'Avoid DNS failures in your Container Apps Environment.'; Tip = ''; LearnMoreLink = 'https://aka.ms/createcontainerapp'; }
        ,[PSCustomObject]@{ Id = 'b9ce2d2e-554b-4391-8ebc-91c570602b04'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Azure Container Apps'; ResourceType = 'microsoft.app/containerapps'; Key = 'ContainerAppCustomDomainCertificateNearExpiration'; Message = 'Renew custom domain certificate'; State = 'Active'; Version = 6; Description = 'Renew custom domain certificate'; LongDescription = 'The custom domain certificate you uploaded is near expiration. To prevent possible service downtime, renew your certificate and upload the new certificate for your container apps.'; Benefits = 'Your service wont fail because of expired certificate.'; Tip = ''; LearnMoreLink = 'https://aka.ms/containerappcustomdomaincert'; }
        ,[PSCustomObject]@{ Id = 'fa6c0880-da2e-42fd-9cb3-e1267ec5b5c2'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure Container Apps'; ResourceType = 'microsoft.app/containerapps'; Key = 'ContainerAppManagedCertificateAutoRenewalFailure'; Message = 'Managed certificate failed to renew.'; State = 'Active'; Version = 6; Description = 'An issue has been detected that is preventing the renewal of your Managed Certificate.'; LongDescription = 'We detected the managed certificate used by the Container App has failed to auto renew. Please follow the documentation link to make sure that the DNS settings of your custom domain are correct.'; Benefits = 'Avoid downtime due to an expired certificate.'; Tip = ''; LearnMoreLink = 'https://aka.ms/containerapps/managed-certificates'; }
        ,[PSCustomObject]@{ Id = '9be5f344-6fa5-4abc-a1f2-61ae6192a075'; Category = 'High Availability'; Control = 'High Availability'; Impact = 'Medium'; ServiceName = 'Azure Container Apps'; ResourceType = 'microsoft.app/containerapps'; Key = 'ContainerAppMinimalReplicaCountTooLow'; Message = 'Increase the minimal replica count for your containerized application'; State = 'Active'; Version = 6; Description = 'Increase the minimal replica count for your containerized application'; LongDescription = 'The minimal replica count set for your Azure Container App containerized application might be too low, which can cause resilience, scalability, and load balancing issues. For better availability, consider increasing the minimal replica count.'; Benefits = 'Better availability for your container app.'; Tip = ''; LearnMoreLink = 'https://aka.ms/containerappscalingrules'; }
        ,[PSCustomObject]@{ Id = 'f6e3ad9c-5d58-48ba-b06b-ebffba60dd18'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Data Factory'; ResourceType = 'microsoft.datafactory/factories/pipelines'; Key = 'ADFFailingPipelines'; Message = 'Delete failing ADF pipelines'; State = 'Active'; Version = 2.2; Description = 'Delete failing ADF pipelines'; LongDescription = 'Repeatedly failing pipelines have been detected in your Azure Data Factory resource. Know that you''re being billed for them even though they are not serving you while failing. Review them to resolve issues or delete failing pipelines that are no longer needed to save on costs.'; Benefits = 'Reduce billing costs by addressing issues with or deleting your failing Data Factory pipelines'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_advisor_adfpipeline'; }
        ,[PSCustomObject]@{ Id = 'eb4f67d2-2440-4d58-bec7-6de73cc5ba75'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Data Factory'; ResourceType = 'microsoft.datafactory/factories/triggers'; Key = 'ADFThrottledTriggers'; Message = 'Review your throttled Data Factory Triggers'; State = 'Active'; Version = 1; Description = 'Review your throttled Data Factory Triggers'; LongDescription = 'A high volume of throttling has been detected in an event-based trigger that runs in your Data Factory resource. This is causing your pipeline runs to drop from the run queue. Review the trigger definition to resolve issues and increase performance.'; Benefits = 'Ensure better performance by reviewing and editing your event-based trigger definition'; Tip = ''; LearnMoreLink = 'https://aka.ms/adf-create-event-trigger'; }
        ,[PSCustomObject]@{ Id = 'a77dd319-ffb5-4f88-bdf2-e98e59afc79f'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformariadb/servers'; Key = 'OrcasMariaDBAuditLog'; Message = 'Increase the reliability of MariaDB audit logs.'; State = 'Active'; Version = 1; Description = 'Increase the reliability of audit logs'; LongDescription = 'Our internal telemetry shows that the server''s audit logs may have been lost over the past day. This can occur when your server is experiencing a CPU heavy workload or a server generates a large number of audit logs over a short period of time. We recommend only logging the necessary events required for your audit purposes using the following server parameters: audit_log_events, audit_log_exclude_users, audit_log_include_users. If the CPU usage on your server is high due to your workload, we recommend increasing the server''s vCores to improve performance.'; Benefits = 'Improve the reliability of audit logs for monitoring and troubleshooting.'; Tip = 'You can improve the reliability of MariaDB audit logs by only logging events you need.'; LearnMoreLink = 'https://aka.ms/mariadb-audit-logs'; }
        ,[PSCustomObject]@{ Id = '860d2d5d-7934-4ccb-a34a-577adf3022a6'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformariadb/servers'; Key = 'OrcasMariaDbConcurrentConnection'; Message = 'Scale the MariaDB server to higher SKU'; State = 'Active'; Version = 1; Description = 'Scale the MariaDB server to higher SKU'; LongDescription = 'Our internal telemetry shows that the server may be unable to support the connection requests because of the maximum supported connections for the given SKU. This may result in a large number of failed connections requests which adversly affect the the performance. To improve performance, we recommend to move to higher memory SKU by increasing vCore or switching to Memory-Optimized SKUs.'; Benefits = 'Improve query performance by allowing more concurrent connections'; Tip = 'You can improve the query performance of your MariaDB database by scaling your server to a higher SKU.'; LearnMoreLink = 'https://aka.ms/mariadbconnectionlimits'; }
        ,[PSCustomObject]@{ Id = 'a5f888e3-8cf4-4491-b2ba-b120e14eb7ce'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformariadb/servers'; Key = 'OrcasMariaDbCpuOverlaod'; Message = 'Increase the MariaDB server vCores'; State = 'Active'; Version = 1; Description = 'Increase the MariaDB server vCores'; LongDescription = 'Our internal telemetry shows that the CPU has been running under high utilization for an extended period of time over the last 7 days. High CPU utilization may lead to slow query performance. To improve performance, we recommend moving to a larger compute size.'; Benefits = 'Improve query performance by reducing CPU pressure'; Tip = 'You can improve the query performance of your MariaDB database by increasing the server vCores.'; LearnMoreLink = 'https://aka.ms/mariadbpricing'; }
        ,[PSCustomObject]@{ Id = 'a092afdb-6f20-4b42-8d8f-423ac8d71a3f'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformariadb/servers'; Key = 'OrcasMariaDbMemoryCache'; Message = 'Move your MariaDB server to Memory Optimzed SKU'; State = 'Active'; Version = 1; Description = 'Move your MariaDB server to Memory Optimzed SKU'; LongDescription = 'Our internal telemetry shows that there is high churn in the buffer pool for this server which can result in slower query performance and increased IOPS. To improve performance, please review your workload queries to identify opportunities to minimize memory consumed.  If no such opportunity found, then we recommend moving to higher SKU with more memory or increase storage size to get more IOPS.'; Benefits = 'Improve query performance by caching more data in memory'; Tip = 'You can improve the query performance of your MariaDB database by moving your server to a Memory Optimized SKU.'; LearnMoreLink = 'https://aka.ms/mariadbpricing'; }
        ,[PSCustomObject]@{ Id = '2af11142-942e-45c1-8ce8-d9d2df25aae9'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformariadb/servers'; Key = 'OrcasMariaDbCpuRightSize'; Message = 'Right-size underutilized MariaDB server'; State = 'Active'; Version = 1.1; Description = 'Right-size underutilized MariaDB servers'; LongDescription = 'Our internal telemetry shows that the MariaDB database server resources have been underutilized for an extended period of time over the last 7 days. Low resource utilization results in unwanted expenditure which can be fixed without significant performance impact. To reduce your costs and efficiently manage your resources, we recommend reducing the compute size (vCores) by half.'; Benefits = 'Reduce cost by right-sizing the MariaDB server'; Tip = 'You can improve the overall resource spend for your Azure Database for MariaDB by scaling down the compute resources by half.'; LearnMoreLink = 'https://aka.ms/mariadbpricing'; }
        ,[PSCustomObject]@{ Id = 'dc791c8d-a74e-4b3e-b7f1-40793399ecd6'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformariadb/servers'; Key = 'OrcasMariaDbStorageLimit'; Message = 'Increase the Server Storage limit'; State = 'Active'; Version = 1; Description = 'Scale the storage limit for MariaDB server'; LongDescription = 'Our internal telemetry shows that the server may be constrained because it is approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned storage amount or turning ON the "Auto-Growth" feature for automatic storage increases'; Benefits = 'Improve query performance by allocating larger storage for the server'; Tip = ''; LearnMoreLink = 'https://aka.ms/mariadbstoragelimits'; }
        ,[PSCustomObject]@{ Id = 'fba7355d-0f26-4015-9b14-17bdc584081a'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMySqlComputeSeriesUpgradeEv5'; Message = 'Upgrade to Ev5 compute series'; State = 'Active'; Version = 2; Description = 'Boost your workload performance by 30% with the new Ev5 compute hardware'; LongDescription = 'With the new Ev5 compute hardware, you can boost workload performance by 30% with higher concurrency and better throughput. Navigate to the Compute+Storage blade on the Azure Portal and switch to Ev5 compute at no additional cost. Ev5 compute provides best performance among other VM series in terms of QPS and latency.'; Benefits = 'With the new Ev5 compute hardware, you can boost workload performance by 30% with higher concurrency and better throughput.'; Tip = 'With the new Ev5 compute hardware, you can boost workload performance by 30% with higher concurrency and better throughput.'; LearnMoreLink = 'https://techcommunity.microsoft.com/t5/azure-database-for-mysql-blog/boost-azure-mysql-business-critical-flexible-server-performance/ba-p/3603698'; }
        ,[PSCustomObject]@{ Id = '5f043aef-0ac4-4dd6-941b-7f4697ebad47'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMysqlConnectionUpsell'; Message = 'Increase connection limit by adding vCores or switching to a higher SKU.'; State = 'Active'; Version = 1.2; Description = 'Scale the MySQL Flexible Server to a higher SKU'; LongDescription = 'Our telemetry indicates that your Flexible Server is exceeding the connection limits associated with your current SKU. A large number of failed connection requests may adversely affect server performance. To improve performance, we recommend increasing the number of vCores or switching to a higher SKU.'; Benefits = 'Improve Flexible Server performance by enabling more concurrent connections.'; Tip = 'You can raise the connection limit of your MySQL Flexible Server by increasing vCores or switching to a higher SKU.'; LearnMoreLink = 'https://aka.ms/azure_mysql_flexible_server_storage'; }
        ,[PSCustomObject]@{ Id = '96181a8c-f81b-45c0-83d2-5c4cf62843c4'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMysqlCpuUpcell'; Message = 'Increase your Flexible Server''s CPU vCore.'; State = 'Active'; Version = 1; Description = 'Increase the MySQL Flexible Server vCores.'; LongDescription = 'Our internal telemetry shows that the CPU has been running under high utilization for an extended period of time over the last 7 days. High CPU utilization may lead to slow query performance. To improve performance, we recommend moving to a larger compute size.'; Benefits = 'Improve query performance by reducing CPU pressure'; Tip = 'You can improve the query performance of your MySQL database by increasing the Flexible Server vCores.'; LearnMoreLink = 'https://aka.ms/azure_mysql_flexible_server_pricing'; }
        ,[PSCustomObject]@{ Id = '89b7919e-60cc-42a3-adb7-2be468f6ecb9'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMysqlMemoryUpsell'; Message = 'Move your MySQL server to Memory Optimized SKU'; State = 'Active'; Version = 1; Description = 'Move your MySQL server to Memory Optimized SKU'; LongDescription = 'Our internal telemetry shows that there is high memory usage for this server which can result in slower query performance and increased IOPS. To improve performance, please review your workload queries to identify opportunities to minimize memory consumed.  If no such opportunity found, then we recommend moving to higher SKU with more memory or increase storage size to get more IOPS.'; Benefits = 'Improve query performance by caching more data in memory'; Tip = 'You can improve the query performance of your MySQL database by moving your server to a Memory Optimized SKU.'; LearnMoreLink = 'https://aka.ms/azure_mysql_flexible_server_storage'; }
        ,[PSCustomObject]@{ Id = 'f9604823-849a-4fe0-b9be-bc937d6b4618'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMysqlReadReplicaUpsell'; Message = 'Add a MySQL Read Replica'; State = 'Active'; Version = 1.1; Description = 'Add a MySQL Read Replica server'; LongDescription = 'Our internal telemetry shows that you may have a read intensive workload running, which results in resource contention for this server. This may lead to slow query performance for the server. To improve performance, we recommend you add a read replica, and offload some of your read workloads to the replica.'; Benefits = 'Improve query performance by scaling out reads'; Tip = 'You can improve the query performance of your MySQL database my adding a Read Replica server.'; LearnMoreLink = 'https://aka.ms/flexible-server-mysql-read-replicas'; }
        ,[PSCustomObject]@{ Id = '6abfe73d-9b26-414c-9e94-62f1db8d653b'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMySqlStorageUpsell'; Message = 'Increase the storage limit for MySQL Flexible Server'; State = 'Active'; Version = 2.5; Description = 'Increase the storage limit for MySQL Flexible Server'; LongDescription = 'Our internal telemetry shows that the server may be constrained because it is approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned storage amount.'; Benefits = 'Improve server performance by increasing the storage limit'; Tip = 'You can improve your MySQL Flexible Server performance by increasing the storage limit.'; LearnMoreLink = 'https://aka.ms/azure_mysql_flexible_server_storage'; }
        ,[PSCustomObject]@{ Id = 'feae9d98-0d24-48eb-ac98-0cedeefd5b9a'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMySqlTlsDeprecation'; Message = 'Deprecating Client communication over TLSv1.0 and TLSv1.1'; State = 'Active'; Version = 1.4; Description = 'Your Azure Database for MySQL - Flexible Server is vulnerable using weak, deprecated TLSv1 or TLSv1.1 protocols'; LongDescription = 'To support modern security standards, MySQL community edition discontinued the support for communication over Transport Layer Security (TLS) 1.0 and 1.1 protocols. Microsoft will also stop supporting connection over TLSv1 and TLSv1.1 to Azure Database for MySQL - Flexible server soon to comply with the modern security standards . We recommend you upgrade your client driver to support TLSv1.2.'; Benefits = 'Secured & Strong TLS protocol for communication'; Tip = 'You can get secured & strong TLS protocol for communication by deprecating the TLSv1.0 and TLSv1.1.'; LearnMoreLink = 'https://aka.ms/encrypted_connection_deprecated_protocols'; }
        ,[PSCustomObject]@{ Id = 'f44c8e21-9f13-4b8e-a839-7141dd5645bb'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/flexibleservers'; Key = 'OrcasMeruMysqlTmpTable'; Message = 'Improve performance by optimizing MySQL temporary-table sizing.'; State = 'Active'; Version = 1; Description = 'Improve performance by optimizing MySQL temporary-table sizing.'; LongDescription = 'Our internal telemetry indicates that your MySQL server may be incurring unncessary I/O overhead due to low temporary-table parameter settings. This may result in unnecessary disk-based transactions and reduced performance. We recommend that you increase the ''tmp_table_size'' and ''max_heap_table_size'' parameter values to reduce the number of disk-based transactions.'; Benefits = 'Improve MySQL workload performance by reducing I/O overhead associated with disk-based transactions'; Tip = 'You can adjust your MySQL temporary-table sizing to improve database performance.'; LearnMoreLink = 'https://dev.mysql.com/doc/refman/8.0/en/internal-temporary-tables.html#internal-temporary-tables-engines'; }
        ,[PSCustomObject]@{ Id = '997839f4-48e4-49e4-9b15-628a7757765c'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLAuditLog'; Message = 'Increase the reliability of MySQL audit logs.'; State = 'Active'; Version = 1; Description = 'Increase the reliability of audit logs'; LongDescription = 'Our internal telemetry shows that the server''s audit logs may have been lost over the past day. This can occur when your server is experiencing a CPU heavy workload or a server generates a large number of audit logs over a short period of time. We recommend only logging the necessary events required for your audit purposes using the following server parameters: audit_log_events, audit_log_exclude_users, audit_log_include_users. If the CPU usage on your server is high due to your workload, we recommend increasing the server''s vCores to improve performance.'; Benefits = 'Improve the reliability of audit logs for monitoring and troubleshooting.'; Tip = 'You can improve the reliability of MySQL audit logs by only logging events you need.'; LearnMoreLink = 'https://aka.ms/mysql-audit-logs'; }
        ,[PSCustomObject]@{ Id = '944611b9-0357-4272-a9ac-a97a65932599'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLConcurrentConnection'; Message = 'Scale the MySQL server to higher SKU'; State = 'Active'; Version = 1; Description = 'Scale the MySQL server to higher SKU'; LongDescription = 'Our internal telemetry shows that the server may be unable to support the connection requests because of the maximum supported connections for the given SKU. This may result in a large number of failed connections requests which adversly affect the the performance. To improve performance, we recommend to move to higher memory SKU by increasing vCore or switching to Memory-Optimized SKUs.'; Benefits = 'Improve query performance by allowing more concurrent connections'; Tip = 'You can improve the query performance of your MySQL database by scaling your server to a higher SKU.'; LearnMoreLink = 'https://aka.ms/mysqlconnectionlimits'; }
        ,[PSCustomObject]@{ Id = 'f62ef41c-2cdb-4f4e-9dc9-a391c579b0fb'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLConnectionPooling'; Message = 'Improve MySQL connection management'; State = 'Active'; Version = 1; Description = 'Improve MySQL connection management'; LongDescription = 'Our internal telemetry indicates that your application connecting to MySQL server may not be managing connections efficiently. This may result in unnecessary resource consumption and overall higher application latency. To improve connection management, we recommend that you reduce the number of short-lived connections and eliminate unnecessary idle connections. This can be done by configuring a server side connection-pooler, such as ProxySQL.'; Benefits = 'Improve performance by reducing overhead associated with short-lived and idle database connections'; Tip = 'You can manage your MySQL database connections more efficiently by configuring a connection pooler, such as ProxySQL.'; LearnMoreLink = 'https://aka.ms/azure_mysql_connection_pooling'; }
        ,[PSCustomObject]@{ Id = '2cbca084-4e80-4720-a7fe-dc8c3074e8ca'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLConnectionRedirection'; Message = 'Improve MySQL connection latency'; State = 'Active'; Version = 1; Description = 'Improve MySQL connection latency'; LongDescription = 'Our internal telemetry indicates that your application connecting to MySQL server may not be managing connections efficiently. This may result in higher application latency. To improve connection latency, we recommend that you enable connection redirection. This can be done by enabling the connection redirection feature of the PHP driver.'; Benefits = 'Reduce network latency between client applications'; Tip = 'You can manage your MySQL database connections more efficiently by enabling connection redirection.'; LearnMoreLink = 'https://aka.ms/azure_mysql_connection_redirection'; }
        ,[PSCustomObject]@{ Id = '0fb3f293-899e-458a-81cc-ad263dd89629'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLCpuOverload'; Message = 'Increase the MySQL server vCores'; State = 'Active'; Version = 1; Description = 'Increase the MySQL server vCores'; LongDescription = 'Our internal telemetry shows that the CPU has been running under high utilization for an extended period of time over the last 7 days. High CPU utilization may lead to slow query performance. To improve performance, we recommend moving to a larger compute size.'; Benefits = 'Improve query performance by reducing CPU pressure'; Tip = 'You can improve the query performance of your MySQL database by increasing the server vCores.'; LearnMoreLink = 'https://aka.ms/mysqlpricing'; }
        ,[PSCustomObject]@{ Id = '74aa92b7-9c42-4640-9b1b-8ab645c86a00'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLMemoryCache'; Message = 'Move your MySQL server to Memory Optimized SKU'; State = 'Active'; Version = 1; Description = 'Move your MySQL server to Memory Optimized SKU'; LongDescription = 'Our internal telemetry shows that there is high churn in the buffer pool for this server which can result in slower query performance and increased IOPS. To improve performance, please review your workload queries to identify opportunities to minimize memory consumed.  If no such opportunity found, then we recommend moving to higher SKU with more memory or increase storage size to get more IOPS.'; Benefits = 'Improve query performance by caching more data in memory'; Tip = 'You can improve the query performance of your MySQL database by moving your server to a Memory Optimized SKU.'; LearnMoreLink = 'https://aka.ms/mysqlpricing'; }
        ,[PSCustomObject]@{ Id = '1efe9592-f5ae-4167-97d7-63e973821fca'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLReadReplica'; Message = 'Add a MySQL Read Replica'; State = 'Active'; Version = 1; Description = 'Add a MySQL Read Replica server'; LongDescription = 'Our internal telemetry shows that you may have a read intensive workload running, which results in resource contention for this server. This may lead to slow query performance for the server. To improve performance, we recommend you add a read replica, and offload some of your read workloads to the replica.'; Benefits = 'Improve query performance by scaling out reads'; Tip = 'You can improve the query performance of your MySQL database my adding a Read Replica server.'; LearnMoreLink = 'https://aka.ms/mysqlreadreplica'; }
        ,[PSCustomObject]@{ Id = '7e76e54f-7978-4d48-9ab9-a4da5b7c69a3'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLCpuRightSize'; Message = 'Right-size underutilized MySQL server'; State = 'Active'; Version = 2.1; Description = 'Right-size underutilized MySQL servers'; LongDescription = 'Our internal telemetry shows that the MySQL database server resources have been underutilized for an extended period of time over the last 7 days. Low resource utilization results in unwanted expenditure which can be fixed without significant performance impact. To reduce your costs and efficiently manage your resources, we recommend reducing the compute size (vCores) by half.'; Benefits = 'Reduce cost by right-sizing the MySQL server'; Tip = 'You can improve the overall resource spend for your Azure Database for MySQL by scaling down the compute resources by half.'; LearnMoreLink = 'https://aka.ms/mysqlpricing'; }
        ,[PSCustomObject]@{ Id = 'c0576597-4910-48b5-9828-5b3a99190b82'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySQLStorageLimit'; Message = 'Increase the Server Storage limit'; State = 'Active'; Version = 1; Description = 'Scale the storage limit for MySQL server'; LongDescription = 'Our internal telemetry shows that the server may be constrained because it is approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned storage amount or turning ON the "Auto-Growth" feature for automatic storage increases'; Benefits = 'Improve query performance by allocating larger storage for the server'; Tip = ''; LearnMoreLink = 'https://aka.ms/mysqlstoragelimits'; }
        ,[PSCustomObject]@{ Id = '99811474-2a6c-4d40-ac91-ae76c76e3258'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbformysql/servers'; Key = 'OrcasMySqlTmpTables'; Message = 'Improve performance by optimizing MySQL temporary-table sizing'; State = 'Active'; Version = 1; Description = 'Improve performance by optimizing MySQL temporary-table sizing'; LongDescription = 'Our internal telemetry indicates that your MySQL server may be incurring unnecessary I/O overhead due to low temporary-table parameter settings. This may result in unnecessary disk-based transactions and reduced performance. We recommend that you increase the ''tmp_table_size'' and ''max_heap_table_size'' parameter values to reduce the number of disk-based transactions.'; Benefits = 'Improve MySQL workload performance by reducing I/O overhead associated with disk-based transactions'; Tip = 'You can adjust your MySQL temporary-table sizing to improve database performance.'; LearnMoreLink = 'https://aka.ms/azure_mysql_tmp_table'; }
        ,[PSCustomObject]@{ Id = 'b26edab6-a8dc-4903-b29f-d3b7fb9e0f9c'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Database for PostgreSQL - Hyperscale (Citus)'; ResourceType = 'microsoft.dbforpostgresql/servergroupsv2'; Key = 'MarlinStorageLimitRecommendation'; Message = 'Increase the storage limit for Hyperscale (Citus) Server Group'; State = 'Active'; Version = 1; Description = 'Increase the storage limit for Hyperscale (Citus) server group'; LongDescription = 'Our internal telemetry shows that one or more nodes in the server group may be constrained because they are approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned disk space.'; Benefits = 'Improve server performance by increasing the storage and continue to use server in read-write mode.'; Tip = 'Increase the storage limit for Hyperscale (Citus) Server Group'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/howto-hyperscale-scale-grow#increase-storage-on-nodes'; }
        ,[PSCustomObject]@{ Id = 'c3c74c9e-e241-496c-be3f-57a2797aa91f'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = ' Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servergroups'; Key = 'OrcasPostgreSqlCitusDistributeData'; Message = 'Distribute data in server group to distribute workload among nodes'; State = 'Active'; Version = 1; Description = 'Distribute data in server group to distribute workload among nodes'; LongDescription = 'It looks like the data has not been distributed in this server group but stays on the coordinator. For full Hyperscale (Citus) benefits distribute data on worker nodes in this server group.'; Benefits = 'Improve query performance by utilizing resource of each node in the server group'; Tip = ''; LearnMoreLink = 'https://go.microsoft.com/fwlink/?linkid=2135201'; }
        ,[PSCustomObject]@{ Id = '426292db-b3e8-46f6-ad3e-d46753943afb'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servergroups'; Key = 'OrcasPostgreSqlCitusRebalanceData'; Message = 'Rebalance data in Hyperscale (Citus) server group to distribute workload among worker nodes more evenly'; State = 'Active'; Version = 1; Description = 'Rebalance data in Hyperscale (Citus) server group to distribute workload among worker nodes more evenly'; LongDescription = 'It looks like the data is not well balanced between worker nodes in this Hyperscale (Citus) server group. In order to use each worker node of the Hyperscale (Citus) server group effectively rebalance data in this server group.'; Benefits = 'Get the most of Hyperscale (Citus) by utilizing resources of each node more evenly'; Tip = ''; LearnMoreLink = 'https://go.microsoft.com/fwlink/?linkid=2148869'; }
        ,[PSCustomObject]@{ Id = '4b0be860-1d79-4360-861a-5382fdb0532c'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlCitusStorageLimitHyperscaleCitus'; Message = 'Increase the storage limit for Hyperscale (Citus) Server Group'; State = 'Active'; Version = 1; Description = 'Increase the storage limit for Hyperscale (Citus) server group'; LongDescription = 'Our internal telemetry shows that one or more nodes in the server group may be constrained because they are approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned disk space.'; Benefits = 'Improve server performance by increasing the storage and continue to use server in read-write mode.'; Tip = 'Increase the storage limit for Hyperscale (Citus) Server Group'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/howto-hyperscale-scale-grow#increase-storage-on-nodes'; }
        ,[PSCustomObject]@{ Id = '6008fcbf-6eb7-48b3-b528-2ec5bae4f94e'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruMeruLogDuration'; Message = 'Set log_duration to OFF'; State = 'Disabled'; Version = 2; Description = 'Optimize log_duration settings for PostgreSQL on Azure Database'; LongDescription = 'Our internal telemetry indicates that you have log_duration enabled, for better performance, set it to OFF'; Benefits = 'Improve server performance by changing log_duration to OFF'; Tip = 'You can improve your PostgreSQL performance by setting log_duration to OFF'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-logging'; }
        ,[PSCustomObject]@{ Id = '523847ec-6157-4e9f-b3ae-01079123a420'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruMeruLogErrorVerbosity'; Message = 'Set log_error_verbosity to DEFAULT'; State = 'Disabled'; Version = 2; Description = 'Optimize log_error_verbosity settings for PostgreSQL on Azure Database'; LongDescription = 'Our internal telemetry indicates that you have log_error_verbosity enabled, for better performance, set it to DEFAULT'; Benefits = 'Improve server performance by changing log_error_verbosity to DEFAULT'; Tip = 'You can improve your PostgreSQL performance by setting log_error_verbosity to DEFAULT'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-logging'; }
        ,[PSCustomObject]@{ Id = '0f5b008d-ab30-4cb6-954f-b66a66d11d37'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruMeruLogMinDuration'; Message = 'Set log_min_duration to -1'; State = 'Disabled'; Version = 2; Description = 'Optimize log_min_duration settings for PostgreSQL on Azure Database'; LongDescription = 'Our internal telemetry indicates that you have log_min_duration enabled, for better performance, set it to -1'; Benefits = 'Improve server performance by changing log_min_duration to -1'; Tip = 'You can improve your PostgreSQL performance by setting log_min_duration to -1'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-logging'; }
        ,[PSCustomObject]@{ Id = 'c3b02a4b-331f-4223-8d5c-20d2d7e439df'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruMeruLogStatement'; Message = 'Set log_statement to NONE'; State = 'Disabled'; Version = 2; Description = 'Optimize log_statement settings for PostgreSQL on Azure Database'; LongDescription = 'Our internal telemetry indicates that you have log_statement enabled, for better performance, set it to NONE'; Benefits = 'Improve server performance by changing log_statement to NONE'; Tip = 'You can improve your PostgreSQL performance by setting log_statement to NONE'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-logging'; }
        ,[PSCustomObject]@{ Id = '7c1948cd-2cd7-4343-9c26-be576842e378'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruMeruQueryCaptureMode'; Message = 'Set pg_qs.query_capture_mode to NONE'; State = 'Disabled'; Version = 2; Description = 'Optimize pg_qs.query_capture_mode settings for PostgreSQL on Azure Database'; LongDescription = 'Our internal telemetry indicates that you have pg_qs.query_capture_mode enabled, for better performance, set it to NONE'; Benefits = 'Improve server performance by changing pg_qs.query_capture_mode to NONE'; Tip = 'You can improve your PostgreSQL performance by setting pg_qs.query_capture_mode to NONE'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-query-store-best-practices'; }
        ,[PSCustomObject]@{ Id = '21160291-a460-4534-9f17-80f038b166f8'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruMeruWorkMem'; Message = 'Increase server parameter: work_mem'; State = 'Disabled'; Version = 2; Description = 'Increase the work_mem to avoid excessive disk spilling from sort and hash'; LongDescription = 'Our internal telemetry shows that the configuration work_mem is too small for your PostgreSQL server which is resulting in disk spilling and degraded query performance. To improve this, we recommend to increase the work_mem limit for the server which will help to reduce the scenarios when the sort or hash happens on disk, thereby improving the overall query performance.'; Benefits = 'Improve query performance by allocating more work_mem for the sort or hash operations thus avoiding unnecessary disk read and write.'; Tip = 'You can improve your PostgreSQL performance by increasing server parameter: work_mem'; LearnMoreLink = 'https://aka.ms/runtimeconfiguration'; }
        ,[PSCustomObject]@{ Id = 'd229f650-eed6-46df-9fe3-977af82e377f'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruOrcasPostgreSQLConnectionPooling'; Message = 'Set pgbouncer.enabled to TRUE'; State = 'Disabled'; Version = 2; Description = 'Optimize PostgreSQL performance by enabling PGBouncer'; LongDescription = 'Our Internal telemetry indiceates that you can improve PostgreSQL performance by enabling PGBouncer'; Benefits = 'Improve server performance by changing pgbouncer.enabled to TRUE'; Tip = 'Improve PostgreSQL performance by enabling PGBouncer'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-pgbouncer'; }
        ,[PSCustomObject]@{ Id = '2b099658-9ec3-4582-af92-e09e90a516da'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasMeruIntelligentTuning'; Message = 'Set Intelligent tuning to ON'; State = 'Disabled'; Version = 2; Description = 'Improve PostgreSQL - Flexible Server performance by enabling Intelligent tuning'; LongDescription = 'Our internal telemetry suggests that you can improve storage performance by enabling Intelligent tuning'; Benefits = 'Improve PostgreSQL - Flexible Server performance by enabling Intelligent tuning'; Tip = 'You can improve your PostgreSQL - Flexible Server performance by enabling Intelligent tuning'; LearnMoreLink = 'https://docs.microsoft.com/azure/postgresql/flexible-server/concepts-intelligent-tuning'; }
        ,[PSCustomObject]@{ Id = '33f26810-57d0-4612-85ff-a83ee9be884a'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasPostgreSqlFlexibleServerLogicalReplicationSlots'; Message = 'Remove inactive logical replication slots'; State = 'Active'; Version = 9; Description = 'Remove inactive logical replication slots (important)'; LongDescription = 'Inactive logical replication slots can result in degraded server performance and unavailability due to write ahead log (WAL) file retention and buildup of snapshot files. Your Azure Database for PostgreSQL flexible server might have inactive logical replication slots. THIS NEEDS IMMEDIATE ATTENTION. Either delete the inactive replication slots, or start consuming the changes from these slots, so that the slots'' Log Sequence Number (LSN) advances and is close to the current LSN of the server.'; Benefits = 'Improve PostgreSQL availability by removing inactive logical replication slots'; Tip = 'You can improve your PostgreSQL availability by removing inactive logical replication slots.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_flexible_server_logical_decoding'; }
        ,[PSCustomObject]@{ Id = '30331dd3-8462-481d-974c-fb5797f323a8'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasMeruMemoryUpsell'; Message = 'Move your PostgreSQL Flexible Server to Memory Optimized SKU'; State = 'Disabled'; Version = 2; Description = 'Move your PostgreSQL Flexible Server to Memory Optimized SKU'; LongDescription = 'Our internal telemetry shows that there is high churn in the buffer pool for this server which can result in slower query performance and increased IOPS. To improve performance, please review your workload queries to identify opportunities to minimize memory consumed. If no such opportunity found, then we recommend moving to higher SKU with more memory or increase storage size to get more IOPS.'; Benefits = 'Improve query performance by caching more data in memory'; Tip = 'You can improve the query performance of your PostgreSQL database by moving your Flexible Server to a Memory Optimized SKU.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_flexible_server_pricing'; }
        ,[PSCustomObject]@{ Id = '2f77028c-f3a0-467c-9dd3-4d29d56ad460'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Database for PostgreSQL - Flexible Server'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlFlexibleServerStorageLimit'; Message = 'Increase the PostgreSQL Flexible Server storage limit'; State = 'Disabled'; Version = 3; Description = 'Increase the storage limit for PostgreSQL Flexible Server'; LongDescription = 'Our internal telemetry shows that the server may be constrained because it is approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned storage amount.'; Benefits = 'Improve server performance by increasing the storage limit'; Tip = 'You can improve your PostgreSQL Flexible Server performance by increasing the storage limit.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_flexible_server_limits'; }
        ,[PSCustomObject]@{ Id = 'f5abcb36-180d-45f7-9a88-f5939823e528'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/flexibleservers'; Key = 'OrcasPostgreSqlMeruMigration'; Message = 'Consider the upgrade to FSPG'; State = 'Disabled'; Version = 2; Description = 'Migrate your database from SSPG to FSPG'; LongDescription = 'Please consider our new offering Azure Database for PostgreSQL Flexible Server that provides richer capabilities such as zone resilient HA, predictable performance, maximum control, custom maintenance window, cost optimization controls and simplified developer experience. Learn more.'; Benefits = 'Please consider our new offering Azure Database for PostgreSQL Flexible Server that provides richer capabilities such as zone resilient HA, predictable performance, maximum control, custom maintenance window, cost optimization controls and simplified developer experience. Learn more.'; Tip = 'Please consider our new offering Azure Database for PostgreSQL Flexible Server that provides richer capabilities such as zone resilient HA, predictable performance, maximum control, custom maintenance window, cost optimization controls and simplified developer experience. Learn more.'; LearnMoreLink = 'https://aka.ms/sspg-upgrade'; }
        ,[PSCustomObject]@{ Id = 'bd109fe8-a2cf-415a-adcb-5e9f9fc1d3c0'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlConcurrentConnection'; Message = 'Scale the PostgreSQL server to higher SKU'; State = 'Disabled'; Version = 2; Description = 'Scale the PostgreSQL server to higher SKU'; LongDescription = 'Our internal telemetry shows that the server may be unable to support the connection requests because of the maximum supported connections for the given SKU. This may result in a large number of failed connections requests which adversly affect the the performance. To improve performance, we recommend to move to higher memory SKU by increasing vCore or switching to Memory-Optimized SKUs.'; Benefits = 'Improve query performance by allowing more concurrent connections'; Tip = 'You can improve the query performance of your PostgreSQL database by scaling your server to a higher SKU.'; LearnMoreLink = 'https://aka.ms/postgresqlconnectionlimits'; }
        ,[PSCustomObject]@{ Id = '84978654-5304-4b2a-81f6-022d18a8b676'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlConnectionPooling'; Message = 'Improve PostgreSQL connection management'; State = 'Disabled'; Version = 2; Description = 'Improve PostgreSQL connection management'; LongDescription = 'Our internal telemetry indicates that your PostgreSQL server may not be managing connections efficiently. This may result in unnecessary resource consumption and overall higher application latency. To improve connection management, we recommend that you reduce the number of short-lived connections and eliminate unnecessary idle connections. This can be done by configuring a server side connection-pooler, such as PgBouncer.'; Benefits = 'Improve performance by reducing overhead associated with short-lived and idle database connections'; Tip = 'You can manage your PostgreSQL database connections more efficiently by configuring a connection pooler, such as PgBouncer.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_connection_pooling'; }
        ,[PSCustomObject]@{ Id = '38500a53-575d-44b7-8f29-6cd534137438'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlCpuOverload'; Message = 'Increase the PostgreSQL server vCores'; State = 'Disabled'; Version = 2; Description = 'Increase the PostgreSQL server vCores'; LongDescription = 'Our internal telemetry shows that the CPU has been running under high utilization for an extended period of time over the last 7 days. High CPU utilization may lead to slow query performance. To improve performance, we recommend moving to a larger compute size.'; Benefits = 'Improve query performance by reducing CPU pressure'; Tip = 'You can improve the query performance of your PostgreSQL database by increasing the server vCores.'; LearnMoreLink = 'https://aka.ms/postgresqlpricing'; }
        ,[PSCustomObject]@{ Id = '993cf5a8-a5c6-4dd6-ac68-fd8c5681d93e'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlLogDuration'; Message = 'Set LogDuration to OFF'; State = 'Disabled'; Version = 2; Description = 'Optimize logging settings by setting LogDuration to OFF'; LongDescription = 'Optimize logging settings by setting LogDuration to OFF'; Benefits = 'Improve server performance I/O'; Tip = 'You can improve your PostgreSQL performance by setting LogDuration to OFF'; LearnMoreLink = 'N/A'; }
        ,[PSCustomObject]@{ Id = '661dd0c2-5d39-4cfb-ba57-59808643e36d'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlLogErrorVerbosity'; Message = 'Adjust server parameter: log_error_verbosity'; State = 'Disabled'; Version = 2; Description = 'Improve PostgreSQL log performance'; LongDescription = 'Our internal telemetry indicates that your PostgreSQL server has been configured to output VERBOSE error logs. This can be useful for troubleshooting your database, but it can also result in reduced database performance. To improve performance, we recommend that you change the log_error_verbosity parameter to the DEFAULT setting.'; Benefits = 'Improve database performance by changing the log_error_verbosity parameter to the DEFAULT setting'; Tip = 'You can improve your PostgreSQL database performance by changing your log_error_verbosity setting.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_log_settings'; }
        ,[PSCustomObject]@{ Id = '624b0218-0a0e-4b48-a961-33dbafdebb47'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlLoggingCollector'; Message = 'Set LoggingCollector to OFF'; State = 'Disabled'; Version = 2; Description = 'Optimize logging settings by setting LoggingCollector to OFF'; LongDescription = 'Optimize logging settings by setting LoggingCollector to OFF'; Benefits = 'Improve server performance I/O'; Tip = 'You can improve your PostgreSQL performance by setting LoggingCollector to OFF'; LearnMoreLink = 'N/A'; }
        ,[PSCustomObject]@{ Id = '6f33a917-418c-4608-b34f-4ff0e7be8637'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlLogicalReplicationSlots'; Message = 'Remove inactive logical replication slots'; State = 'Active'; Version = 9; Description = 'Remove inactive logical replication slots'; LongDescription = 'When an Orcas PostgreSQL flexible server has inactive logical replication slots, degraded server performance and unavailability due to write ahead log (WAL) file retention and buildup of snapshot files might occur. THIS NEEDS IMMEDIATE ATTENTION. Either delete the inactive replication slots, or start consuming the changes from these slots, so that the slots'' Log Sequence Number (LSN) advances and is close to the current LSN of the server.'; Benefits = 'Improve PostgreSQL availability by removing inactive logical replication slots'; Tip = 'You can improve your PostgreSQL availability by removing inactive logical replication slots.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_logical_decoding'; }
