        ,[PSCustomObject]@{ Id = '47b11ec4-7950-43a1-b6b5-f051f812bd34'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlMemoryCache'; Message = 'Move your PostgreSQL server to Memory Optimized SKU'; State = 'Disabled'; Version = 2; Description = 'Move your PostgreSQL server to Memory Optimized SKU'; LongDescription = 'Our internal telemetry shows that there is high churn in the buffer pool for this server which can result in slower query performance and increased IOPS. To improve performance, please review your workload queries to identify opportunities to minimize memory consumed.  If no such opportunity found, then we recommend moving to higher SKU with more memory or increase storage size to get more IOPS.'; Benefits = 'Improve query performance by caching more data in memory'; Tip = 'You can improve the query performance of your PostgreSQL database by moving your server to a Memory Optimized SKU.'; LearnMoreLink = 'https://aka.ms/postgresqlpricing'; }
        ,[PSCustomObject]@{ Id = 'feba5625-68c8-4b53-90b4-8a9de4f71e9e'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlQueryCaptureMode'; Message = 'Set pg_qs.query_capture_mode to NONE when not troubleshooting'; State = 'Disabled'; Version = 3; Description = 'Optimize query store on an Azure Database for PostgreSQL when not troubleshooting'; LongDescription = 'Our internal telemetry indicates that your PostgreSQL database has been configured to track query performance using the pg_qs.query_capture_mode parameter. While troubleshooting, we suggest setting the pg_qs.query_capture_mode parameter to TOP or ALL. When not troubleshooting, we recommend that you set the pg_qs.query_capture_mode parameter to NONE.'; Benefits = 'Optimize PostgreSQL performance by changing the pg_qs.query_capture_mode parameter to NONE when not troubleshooting'; Tip = 'You can optimize your PostgreSQL performance by changing your pg_qs.query_capture_mode setting to NONE when not troubleshooting.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_query_store'; }
        ,[PSCustomObject]@{ Id = '8fd46c9b-5ba1-4133-8a5d-dfc61e1195b1'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlReadReplica'; Message = 'Add PostgreSQL Read Replica'; State = 'Disabled'; Version = 2; Description = 'Add a PostgreSQL Read Replica server'; LongDescription = 'Our internal telemetry shows that you may have a read intensive workload running, which results in resource contention for this server. This may lead to slow query performance for the server. To improve performance, we recommend you add a read replica, and offload some of your read workloads to the replica.'; Benefits = 'Improve query performance by scaling out reads'; Tip = 'You can improve the query performance of your PostgreSQL database my adding a Read Replica server.'; LearnMoreLink = 'https://aka.ms/postgresqlreadreplica'; }
        ,[PSCustomObject]@{ Id = 'a8604ddc-7087-4752-b0b3-8fed1a5a37a6'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlReplaceParameter'; Message = 'Set ReplaceParameter to OFF'; State = 'Disabled'; Version = 2; Description = 'Optimize logging settings by setting ReplaceParameter to OFF'; LongDescription = 'Optimize logging settings by setting ReplaceParameter to OFF'; Benefits = 'Improve server performance I/O'; Tip = 'You can improve your PostgreSQL performance by setting ReplaceParameter to OFF'; LearnMoreLink = 'N/A'; }
        ,[PSCustomObject]@{ Id = '38f4a461-1543-4089-854c-90e7edf37707'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlCpuRightSize'; Message = 'Right-size underutilized PostgreSQL server'; State = 'Disabled'; Version = 3.1; Description = 'Right-size underutilized PostgreSQL servers'; LongDescription = 'Our internal telemetry shows that the PostgreSQL database server resources have been underutilized for an extended period of time over the last 7 days. Low resource utilization results in unwanted expenditure which can be fixed without significant performance impact. To reduce your costs and efficiently manage your resources, we recommend reducing the compute size (vCores) by half.'; Benefits = 'Reduce cost by right-sizing the PostgreSQL server'; Tip = 'You can improve the overall resource spend for your Azure Database for PostgreSQL by scaling down the compute resources by half.'; LearnMoreLink = 'https://aka.ms/postgresqlpricing'; }
        ,[PSCustomObject]@{ Id = 'b8d89adf-9b67-4c44-8a08-5f40f5a5f1ab'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlStatStatementsTrack'; Message = 'Set pg_stat_statements.track to NONE'; State = 'Disabled'; Version = 2; Description = 'Optimize query statistics collection on an Azure Database for PostgreSQL'; LongDescription = 'Our internal telemetry indicates that your PostgreSQL server has been configured to track query statistics using the pg_stat_statements module. While useful for troubleshooting, it can also result in reduced server performance. To improve performance, we recommend that you change the pg_stat_statements.track parameter to NONE.'; Benefits = 'Improve server performance by changing the pg_stat_statements.track parameter to NONE'; Tip = 'You can improve your PostgreSQL server performance by changing your pg_stat_statements.track setting to NONE.'; LearnMoreLink = 'https://aka.ms/azure_postgresql_optimize_query_stats'; }
        ,[PSCustomObject]@{ Id = 'ae2b8ab9-f6b9-4531-ba04-44f00880dc18'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlStorageLimit'; Message = 'Increase the Server Storage limit'; State = 'Disabled'; Version = 2; Description = 'Scale the storage limit for PostgreSQL server'; LongDescription = 'Our internal telemetry shows that the server may be constrained because it is approaching limits for the currently provisioned storage values. This may result in degraded performance or in the server being moved to read-only mode. To ensure continued performance, we recommend increasing the provisioned storage amount or turning ON the "Auto-Growth" feature for automatic storage increases'; Benefits = 'Improve query performance by allocating larger storage for the server'; Tip = ''; LearnMoreLink = 'https://aka.ms/postgresqlstoragelimits'; }
        ,[PSCustomObject]@{ Id = 'e2fa79e2-b3e0-11e9-a2a3-2a2ae2dbcce4'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Open Source RDBMS (Orcas)'; ResourceType = 'microsoft.dbforpostgresql/servers'; Key = 'OrcasPostgreSqlWorkMem'; Message = 'Increase server parameter: work_mem'; State = 'Disabled'; Version = 2; Description = 'Increase the work_mem to avoid excessive disk spilling from sort and hash'; LongDescription = 'Our internal telemetry shows that the configuration work_mem is too small for your PostgreSQL server which is resulting in disk spilling and degraded query performance. To improve this, we recommend to increase the work_mem limit for the server which will help to reduce the scenarios when the sort or hash happens on disk, thereby improving the overall query performance.'; Benefits = 'Improve query performance by allocating more work_mem for the sort or hash operations thus avoiding unnecessary disk read and write.'; Tip = ''; LearnMoreLink = 'https://aka.ms/runtimeconfiguration'; }
        ,[PSCustomObject]@{ Id = 'dd8bef5d-5a56-4239-b25b-a25f283e12f5'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Health AI Deidentification Service'; ResourceType = 'microsoft.healthdataaiservices/deidservices'; Key = 'UpgradeDeIDAPIVersion'; Message = 'Upgrade to the latest Azure Health De-Identification API Version'; State = 'Active'; Version = 1; Description = 'Upgrade to latest API Version for Azure Health De-Identification'; LongDescription = 'Upgrading to the latest API Version for Azure Health De-Identification ensures maximal performance. If using the Health De-Identification SDK, upgrade to the most recent package version. Alternatively, you can use the Azure Portal to create/manage Health De-Identification resources.'; Benefits = 'Improved reliability and performance'; Tip = 'Keep your API version up-to-date for the best performance and reliability'; LearnMoreLink = 'https://learn.microsoft.com/en-us/azure/healthcare-apis/deidentification/'; }
        ,[PSCustomObject]@{ Id = 'ed25ee86-c6b4-4485-a129-3c0ec1b523da'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Health AI Deidentification Service'; ResourceType = 'microsoft.healthdataaiservices/deidservices'; Key = 'UpgradeDeIDDataPlaneAPIVersion'; Message = 'Use latest Azure Health De-Identification data plane API version'; State = 'Active'; Version = 1; Description = 'Use newest API version for Azure Health De-Identification'; LongDescription = 'Use the newest API version for Azure Health De-Identification. For the Health De-Identification SDK, upgrade to the newest package version. Verify all API requests include the api-version query parameter.'; Benefits = 'Newest API version offers enhanced throughput and security.'; Tip = 'Ensure your API version is up-to-date for the best performance and reliability'; LearnMoreLink = 'https://learn.microsoft.com/azure/healthcare-apis/deidentification'; }
        ,[PSCustomObject]@{ Id = '061dcd4a-2090-4ec0-b4e0-ec9eaae5cf80'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBAttachments'; Message = 'Migrate Azure Cosmos DB attachments to Azure Blob Storage'; State = 'Active'; Version = 2; Description = 'Migrate Azure Cosmos DB attachments to Azure Blob Storage'; LongDescription = 'We noticed that your Azure Cosmos collection is using the legacy attachments feature. We recommend migrating attachments to Azure Blob Storage to improve the resiliency and scalability of your blob data.'; Benefits = 'Improve attachment blob resiliency and scalability'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/attachments#migrating-attachments-to-azure-blob-storage'; }
        ,[PSCustomObject]@{ Id = 'cdf51428-a41b-4735-ba23-39f3b7cde20c'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBAutoscaleRecommendations'; Message = 'Enable autoscale on your Azure Cosmos DB database or container'; State = 'Active'; Version = 1.1; Description = 'Enable autoscale on your Azure Cosmos DB database or container'; LongDescription = 'Based on your usage in the past 7 days, you can save by enabling autoscale. For each hour, we compared the RU/s provisioned to the actual utilization of the RU/s (what autoscale would have scaled to) and calculated the cost savings across the time period. Autoscale helps optimize your cost by scaling down RU/s when not in use.'; Benefits = 'Optimize Azure spend'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/provision-throughput-autoscale'; }
        ,[PSCustomObject]@{ Id = '683b5e32-48aa-4b46-a822-4e22a20ee244'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBDefaultIndexingWithManyPaths'; Message = 'Optimize your Azure Cosmos DB indexing policy to only index what''s needed'; State = 'Active'; Version = 1; Description = 'Optimize your Azure Cosmos DB indexing policy to only index what''s needed'; LongDescription = 'Your Azure Cosmos DB containers are using the default indexing policy, which indexes every property in your documents. Because you''re storing large documents, a high number of properties get indexed, resulting in high Request Unit consumption and poor write latency. To optimize write performance, we recommend overriding the default indexing policy to only index the properties used in your queries.'; Benefits = 'Improve the write throughput of your container'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/index-policy'; }
        ,[PSCustomObject]@{ Id = '5e4e9f04-9201-4fd9-8af6-a9539d13d8ec'; Category = 'High Availability'; Control = 'Scalability '; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBFixedCollections'; Message = 'Configure Azure Cosmos DB containers with a partition key'; State = 'Active'; Version = 8; Description = 'Configure Azure Cosmos DB containers with a partition key'; LongDescription = 'When Azure Cosmos DB nonpartitioned collections reach their provisioned storage quota, you lose the ability to add data. Your Cosmos DB nonpartitioned collections are approaching their provisioned storage quota. Migrate these collections to new collections with a partition key definition so they can automatically be scaled out by the service.'; Benefits = 'Scale your containers seamlessly with increase in storage or request rates without running into any limits'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/partitioning-overview#choose-partitionkey'; }
        ,[PSCustomObject]@{ Id = '4a993d7c-9d83-4d85-b5a9-7cce0b136378'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBFreeTierOverage'; Message = 'Review the configuration of your Azure Cosmos DB free tier account'; State = 'Active'; Version = 3.2; Description = 'Review the configuration of your Azure Cosmos DB free tier account'; LongDescription = 'Your Azure Cosmos DB free tier account is currently containing resources with a total provisioned throughput exceeding 1000 Request Units per second (RU/s). Because Azure Cosmos DB''s free tier only covers the first 1000 RU/s of throughput provisioned across your account, any throughput beyond 1000 RU/s are billed at the regular pricing. As a result, we anticipate that you are charged for the throughput currently provisioned on your Azure Cosmos DB account.'; Benefits = 'Confirm your expected Azure Cosmos DB costs'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/understand-your-bill#azure-free-tier'; }
        ,[PSCustomObject]@{ Id = '75c8c891-46d2-41fa-a81c-84e870a139a9'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBGatewayMode'; Message = 'Configure your Azure Cosmos DB applications to use Direct connectivity in the SDK'; State = 'Active'; Version = 3.1; Description = 'Configure your Azure Cosmos DB applications to use Direct connectivity in the SDK'; LongDescription = 'We noticed that your Azure Cosmos DB applications are using Gateway mode via the Cosmos DB .NET or Java SDKs. We recommend switching to Direct connectivity for lower latency and higher scalability.'; Benefits = 'Improved latency and high availability for your applications'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/performance-tips#networking'; }
        ,[PSCustomObject]@{ Id = '3a7c4990-18e7-4581-b62d-c745260e7c5b'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBHierarchicalPartitionKey'; Message = 'Use hierarchical partition keys for optimal data distribution'; State = 'Active'; Version = 1; Description = 'Use hierarchical partition keys for optimal data distribution'; LongDescription = 'This account has a custom setting that allows the logical partition size in a container to exceed the limit of 20 GB. This setting was applied by the Azure Cosmos DB team as a temporary measure to give you time to re-architect your application with a different partition key. It is not recommended as a long-term solution, as SLA guarantees are not honored when the limit is increased. You can now use hierarchical partition keys (preview) to re-architect your application. The feature allows you to exceed the 20 GB limit by setting up to three partition keys, ideal for multi-tenant scenarios or workloads that use synthetic keys.'; Benefits = 'Optimize data distribution and performance'; Tip = ''; LearnMoreLink = 'https://devblogs.microsoft.com/cosmosdb/hierarchical-partition-keys-private-preview/'; }
        ,[PSCustomObject]@{ Id = 'bdb595a4-e148-41f9-98e8-68ec92d1932e'; Category = 'High Availability'; Control = 'Scalability '; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBHighMetadataOperations'; Message = 'Use static Cosmos DB client instances in your code and cache the names of databases and collections'; State = 'Active'; Version = 6; Description = 'Use static Cosmos DB client instances in your code and cache the names of databases and collections'; LongDescription = 'A high number of metadata operations on an account can result in rate limiting. Metadata operations have a system-reserved request unit (RU) limit. Avoid rate limiting from metadata operations by using static Cosmos DB client instances in your code and caching the names of databases and collections.'; Benefits = 'Optimize your RU usage and avoid rate limiting'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/performance-tips'; }
        ,[PSCustomObject]@{ Id = 'a4255ba5-b07e-45ae-99ca-25e6c2079e3c'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBIdleContainers'; Message = 'Decrease provisioned throughput or delete your idle Azure Cosmos DB containers'; State = 'Active'; Version = 3; Description = 'Consider taking action on your idle Azure Cosmos DB containers'; LongDescription = 'We haven''t detected any activity over the past 30 days on one or more of your Azure Cosmos DB containers. Consider lowering their throughput, or deleting them if you don''t plan on using them.'; Benefits = 'Optimize Azure spend'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/how-to-provision-container-throughput'; }
        ,[PSCustomObject]@{ Id = '44a0a07f-23a2-49df-b8dc-a1b14c7c6a9d'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBKeyVaultWrap'; Message = 'Your Cosmos DB account is unable to access its linked Azure Key Vault hosting your encryption key'; State = 'Active'; Version = 6; Description = 'Check linked Azure Key Vault hosting your encryption key'; LongDescription = 'When an Azure Cosmos DB account can''t access its linked Azure Key Vault hosting the encyrption key, data access and security issues might happen. Your Azure Key Vault''s configuration is preventing your Cosmos DB account from contacting the key vault to access your managed encryption keys. If you  recently performed a key rotation, ensure that the previous key, or key version, remains enabled and available until Cosmos DB completes the rotation. The previous key or key version can be disabled after 24 hours, or after the Azure Key Vault audit logs don''t show any activity from Azure Cosmos DB on that key or key version.'; Benefits = 'Update your configurations to continue using customer-managed keys and access your data'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/how-to-setup-cmk'; }
        ,[PSCustomObject]@{ Id = '213974c8-ed9c-459f-9398-7cdaa3c28856'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBLazyIndexing'; Message = 'Consider Consistent indexing mode on Azure Cosmos DB containers'; State = 'Active'; Version = 7; Description = 'Configure consistent indexing mode on Azure Cosmos DB containers'; LongDescription = 'Azure Cosmos containers configured with the Lazy indexing mode update asynchronously, which improves write performance, but can impact query freshness. Your container is configured with the Lazy indexing mode. If query freshness is critical, use Consistent Indexing Mode for immediate index updates.'; Benefits = 'Improve query result consistency and reliability'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/how-to-manage-indexing-policy'; }
        ,[PSCustomObject]@{ Id = 'bc9e5110-a220-4ab9-8bc9-53f92d3eef70'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMaxGlobalLSNReachedV2'; Message = 'Upgrade to 2.6.14 version of the Async Java SDK v2 to avoid a critical issue or upgrade to Java SDK v4 as Async Java SDK v2 is being deprecated'; State = 'Active'; Version = 6; Description = 'Hotfix - Upgrade to 2.6.14 version of the Async Java SDK v2 or to Java SDK v4'; LongDescription = 'There''s a critical bug in version 2.6.13 (and lower) of the Azure Cosmos DB Async Java SDK v2 causing errors when a Global logical sequence number (LSN) greater than the Max Integer value is reached. The error happens transparently to you by the service after a large volume of transactions occur in the lifetime of an Azure Cosmos DB container. Note: While this is a critical hotfix for the Async Java SDK v2, we still highly recommend you migrate to the [Java SDK v4](https://docs.microsoft.com/azure/cosmos-db/sql/sql-api-sdk-java-v4).'; Benefits = 'If action isn''t taken, all create, read, update, and delete operations may begin to fail with NumberFormatException'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/sql/sql-api-sdk-async-java'; }
        ,[PSCustomObject]@{ Id = '38942ae5-3154-4e0b-98d9-23aa061c334b'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMaxGlobalLSNReachedV4'; Message = 'Upgrade to the current recommended version of the Java SDK v4 to avoid a critical issue'; State = 'Active'; Version = 6; Description = 'Critical issue - Upgrade to the current recommended version of the Java SDK v4'; LongDescription = 'There''s a critical bug in version 4.15 and lower of the Azure Cosmos DB Java SDK v4 causing errors when a Global logical sequence number (LSN) greater than the Max Integer value is reached. This happens transparently to you by the service after a large volume of transactions occur in the lifetime of an Azure Cosmos DB container. Avoid this problem by upgrading to the current recommended version of the Java SDK v4'; Benefits = 'If action isn''t taken, all create, read, update, and delete operations may begin to fail with NumberFormatException'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/sql/sql-api-sdk-java-v4'; }
        ,[PSCustomObject]@{ Id = '52fef986-5897-4359-8b92-0f22749f0d73'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMigrateToContinuousBackup'; Message = 'Improve resiliency by migrating your Azure Cosmos DB accounts to continuous backup'; State = 'Active'; Version = 1.1; Description = 'Improve resiliency by migrating your Azure Cosmos DB accounts to continuous backup'; LongDescription = 'Your Azure Cosmos DB accounts are configured with periodic backup. Continuous backup with point-in-time restore is now available on these accounts. With continuous backup, you can restore your data to any point in time within the past 30 days. Continuous backup may also be more cost-effective as a single copy of your data is retained.'; Benefits = 'Improve the resiliency of your Azure Cosmos DB workloads'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/continuous-backup-restore-introduction'; }
        ,[PSCustomObject]@{ Id = '6aa7a0df-192f-4dfa-bd61-f43db4843e7d'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMigrateToManualThroughputFromAutoscale'; Message = 'Configure manual throughput instead of autoscale on your Azure Cosmos DB database or container'; State = 'Active'; Version = 2.1; Description = 'Configure manual throughput instead of autoscale on your Azure Cosmos DB database or container'; LongDescription = 'Based on your usage in the past 7 days, you can save by using manual throughput instead of autoscale. Manual throughput is more cost-effective when average utilization of your maximum throughput (RU/s) is greater than 66% or less than or equal to 10%.'; Benefits = 'Optimize Azure spend'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/how-to-choose-offer'; }
        ,[PSCustomObject]@{ Id = 'ceb9372d-60f6-4564-8033-a8b1ead4fa76'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMongoMigrationUpgrade'; Message = 'Migrate your Azure Cosmos DB API for MongoDB account to v4.2 to save on query/storage costs and utilize new features'; State = 'Active'; Version = 2.1; Description = 'Migrate your Azure Cosmos DB API for MongoDB account to v4.2 to save on query/storage costs and utilize new features'; LongDescription = 'Migrate your database account to a new database account to take advantage of Azure Cosmos DB''s API for MongoDB v4.2. Upgrading to v4.20 can reduce your storage costs by up to 55% and your query costs by up to 45% by leveraging a new storage format. Numerous other features such as multi-document transactions are also included in v4.2. When upgrading, you must also migrate the data in your existing account to a new account created using version 4.2. Azure Data Factory or Studio 3T can assist you in migrating your data.'; Benefits = 'Improved reliability, query/storage efficiency, performance, and new feature capabilities'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/mongodb/feature-support-42'; }
        ,[PSCustomObject]@{ Id = '123039b5-0fda-4744-9a17-d6b5d5d122b2'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMongoNudge36AwayFrom32'; Message = 'Use the new 3.6+ endpoint to connect to your upgraded Azure Cosmos DB''s API for MongoDB account'; State = 'Active'; Version = 6; Description = 'Use the new 3.6+ endpoint to connect to your upgraded Azure Cosmos DB''s API for MongoDB account'; LongDescription = 'Some of your applications are connecting to your upgraded Azure Cosmos DB''s API for MongoDB account using the legacy 3.2 endpoint - [accountname].documents.azure.com. Use the new endpoint - [accountname].mongo.cosmos.azure.com (or its equivalent in sovereign, government, or restricted clouds).'; Benefits = 'Take advantage of the latest features in version 3.6+ of Azure Cosmos DB''s API for MongoDB'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/mongodb-feature-support-40'; }
        ,[PSCustomObject]@{ Id = '0da795d9-26d2-4f02-a019-0ec383363c88'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMongoSelfServeUpgrade'; Message = 'Upgrade your Azure Cosmos DB API for MongoDB account to v4.2 to save on query/storage costs and utilize new features'; State = 'Active'; Version = 7; Description = 'Upgrade your Azure Cosmos DB API for MongoDB account to v4.2 to save on query/storage costs and utilize new features'; LongDescription = 'Your Azure Cosmos DB API for MongoDB account is eligible to upgrade to version 4.2. Upgrading to v4.2 can reduce your storage costs by up to 55% and your query costs by up to 45% by leveraging a new storage format. Numerous additional features such as multi-document transactions are also included in v4.2.'; Benefits = 'Improved reliability, query/storage efficiency, performance, and new feature capabilities'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/mongodb-version-upgrade'; }
        ,[PSCustomObject]@{ Id = 'ec6fe20c-08d6-43da-ac18-84ac83756a88'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBMongoServerSideRetries'; Message = 'Enable Server Side Retry (SSR) on your Azure Cosmos DB''s API for MongoDB account'; State = 'Active'; Version = 6; Description = 'Enable Server Side Retry (SSR) on your Azure Cosmos DB''s API for MongoDB account'; LongDescription = 'When an account is throwing a TooManyRequests error with the 16500 error code, enabling Server Side Retry (SSR) can help mitigate the issue.'; Benefits = 'Prevent throttling and improve your query reliability and performance'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/prevent-rate-limiting-errors'; }
        ,[PSCustomObject]@{ Id = '8b993855-1b3f-4392-8860-6ed4f5afd8a7'; Category = 'Cost'; Control = ''; Impact = 'Low'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBOptimizeRU'; Message = 'Optimize RU provisioning for Azure Cosmos DB'; State = 'Active'; Version = 2; Description = 'Provision the optimal amount of Request Units for Azure Cosmos DB'; LongDescription = 'This recommendation analyzes usage data in the past 3 weeks and recommends the optimal amount of Request Units to provision for an Azure Cosmos DB. The recommendation is based on the maximum Request Units used in every 5-minute interval and any throttling reports in the 3-week period.'; Benefits = 'Optimize Azure spend'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_cosmosdb_ru_learnmore'; }
        ,[PSCustomObject]@{ Id = '0f04b7d8-55cd-4cac-b324-d7ed9b11fab0'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBOrderByHighRUCharge'; Message = 'Add composite indexes to your Azure Cosmos DB container'; State = 'Disabled'; Version = 2; Description = 'Add composite indexes to your Azure Cosmos DB container'; LongDescription = 'Your Azure Cosmos DB containers are running ORDER BY queries incurring high Request Unit (RU) charges. It is recommended to add composite indexes to your containers'' indexing policy to improve the RU consumption and decrease the latency of these queries.'; Benefits = 'Run ORDER BY queries more efficiently'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/index-policy#composite-indexes'; }
        ,[PSCustomObject]@{ Id = 'bf161e78-ce57-4198-82e8-a34522045518'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBPartitionMerge'; Message = 'Enable partition merge to configure an optimal database partition layout'; State = 'Active'; Version = 3.2; Description = 'Enable partition merge to configure an optimal database partition layout'; LongDescription = 'Your account has collections that could benefit from enabling partition merge. Minimizing the number of partitions will reduce rate limiting and resolve storage fragmentation problems. Containers are likely to benefit from this if the RU/s per physical partition is < 3000 RUs and storage is < 20 GB.'; Benefits = 'Improve performance and lower the chance of rate-limiting'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/merge?tabs=azure-powershell'; }
        ,[PSCustomObject]@{ Id = 'bf161e78-ce57-4198-82e8-a34522045518'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBPartitionMerge'; Message = 'Enable partition merge to configure an optimal database partition layout'; State = 'Active'; Version = 3.3; Description = 'Enable partition merge to configure an optimal database partition layout'; LongDescription = 'Your account has collections that could benefit from enabling partition merge. Minimizing the number of partitions will reduce rate limiting and resolve storage fragmentation problems. Containers are likely to benefit from this if the RU/s per physical partition is < 3000 RUs and storage is < 20 GB.'; Benefits = 'Improve performance and lower the chance of rate-limiting'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/merge?tabs=azure-powershell'; }
        ,[PSCustomObject]@{ Id = 'e27c5181-5005-4dc3-a449-89b726a3bf54'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBQueryPageSize'; Message = 'Configure your Azure Cosmos DB query page size (MaxItemCount) to -1'; State = 'Active'; Version = 2.1; Description = 'Configure your Azure Cosmos DB query page size (MaxItemCount) to -1'; LongDescription = 'You are using the query page size of 100 for queries for your Azure Cosmos container. We recommend using a page size of -1 for faster scans.'; Benefits = 'End to end query latency will be improved significantly'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/sql-api-query-metrics#max-item-count'; }
        ,[PSCustomObject]@{ Id = 'b57f7a29-dcc8-43de-86fa-18d3f9d3764d'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBSingleRegionProdAccounts'; Message = 'Add a second region to your production workloads on Azure Cosmos DB'; State = 'Active'; Version = 6; Description = 'Add a second region to your production workloads on Azure Cosmos DB'; LongDescription = 'Production workloads on Azure Cosmos DB run in a single region might have availability issues, this appears to be the case with some of your Cosmos DB accounts. Increase their availability by configuring them to span at least two Azure regions. NOTE: Additional regions incur additional costs.'; Benefits = 'Improve the availability of your production workloads'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/high-availability'; }
        ,[PSCustomObject]@{ Id = '893a1342-e1f6-49ec-97dc-c5c4471438c2'; Category = 'Cost'; Control = ''; Impact = 'Low'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBOptimizeUnusedContainer'; Message = 'Optimize Unused Containers for Azure Cosmos DB'; State = 'Disabled'; Version = 1.1; Description = 'Consider taking action on your unused containers'; LongDescription = 'This recommendation shows if Azure Cosmos DB containers and/or geo-replications are unused by looking at their usage data for the past 30 days. It is recommended to reduce the provisioned throughput to the configuration minimum if the stored data is still needed, or delete the container if it is no longer needed. If you have geo-replication enabled, ensure that you''re using all the regions chosen.'; Benefits = 'Optimize Azure spend'; Tip = ''; LearnMoreLink = 'https://aka.ms/cosmosdb/usage-cost'; }
        ,[PSCustomObject]@{ Id = '51a4e6bd-5a95-4a41-8309-40f5640fdb8b'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBUpgradeOldSDK'; Message = 'Upgrade your old Azure Cosmos DB SDK to the latest version'; State = 'Active'; Version = 11; Description = 'Upgrade old Azure Cosmos DB SDK to the latest version'; LongDescription = 'An Azure Cosmos DB account using an old version of the SDK lacks the latest fixes and improvements. Your Azure Cosmos DB account is using an old version of the SDK. For the latest fixes, performance improvements, and new feature capabilities, upgrade to the latest version.'; Benefits = 'Improved reliability, performance, and new feature capabilities'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/'; }
        ,[PSCustomObject]@{ Id = '60a55165-9ccd-4536-81f6-e8dc6246d3d2'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBUpgradeOutdatedSDK'; Message = 'Upgrade your deprecated Azure Cosmos DB SDK to a supported version'; State = 'Active'; Version = 10; Description = 'Upgrade outdated Azure Cosmos DB SDK to the latest version'; LongDescription = 'An Azure Cosmos DB account using an old version of the SDK lacks the latest fixes and improvements. Your Azure Cosmos DB account is using an outdated version of the SDK. We recommend upgrading to the latest version for the latest fixes, performance improvements, and new feature capabilities.'; Benefits = 'Improved reliability, performance, and new feature capabilities'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/'; }
        ,[PSCustomObject]@{ Id = 'bf161e78-ce57-4198-82e8-a34522045518'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Azure Cosmos DB'; ResourceType = 'microsoft.documentdb/databaseaccounts'; Key = 'CosmosDBPartitionMerge'; Message = 'Enable partition merge to configure an optimal database partition layout'; State = 'Active'; Version = 4.7; Description = 'Enable partition merge to configure an optimal database partition layout'; LongDescription = 'Your account has collections that could benefit from enabling partition merge. Minimizing the number of partitions will reduce rate limiting and resolve storage fragmentation problems. Containers are likely to benefit from this if the RU/s per physical partition is < 3000 RUs and storage is < 20 GB.'; Benefits = 'Improve performance and lower the chance of rate-limiting'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cosmos-db/merge?tabs=azure-powershell'; }
        ,[PSCustomObject]@{ Id = 'dc045941-8e65-437b-992b-1f0acd28bb6e'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Virtual Machines'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'EphemeralOsDisk'; Message = 'Use Ephemeral OS Disk'; State = 'Active'; Version = 4.1; Description = 'Use Virtual Machines with Ephemeral OS Disk enabled to save cost and get better performance'; LongDescription = 'With Ephemeral OS Disk, Customers get these benefits: Save on storage cost for OS disk. Get lower read/write latency to OS disk. Faster VM Reimage operation by resetting OS (and Temporary disk) to its original state. It is more preferable to use Ephemeral OS Disk for short-lived IaaS VMs or VMs with stateless workloads.'; Benefits = 'Reduced storage cost, lower read/write latency, faster reimage operation for OS disk. More preferable for short-lived IaaS VMs or VMs with stateless workloads'; Tip = 'Ephemeral OS disk incurs no storage cost for OS disk and provides lower read/write latency to the OS disk. It also provides faster VM reimage operation to wipe the contents on OS disk and reset the VM to its original state. This significantly reduces the idle time involved with multiple VM create/delete operations.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/windows/ephemeral-os-disks'; }
        ,[PSCustomObject]@{ Id = 'fec68ad4-95b5-4dca-9e20-afa10b3e5401'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'AzureExpressRoute'; Message = 'Experience more predictable, consistent latency with a private connection to Azure'; State = 'Active'; Version = 1.0; Description = 'Experience more predictable, consistent latency with a private connection to Azure'; LongDescription = 'Improve the performance, privacy, and reliability of your business-critical apps by extending your on-premises networks to Azure with Azure ExpressRoute. Establish private ExpressRoute connections directly from your WAN, through a cloud exchange facility, or through POP and IPVPN connections.'; Benefits = 'Fast, private, bandwidth up to 100 Gbps, plus cost benefits'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/expressroute/expressroute-howto-circuit-portal-resource-manager'; }
        ,[PSCustomObject]@{ Id = '835e829e-9af6-4758-80d2-98df565d1524'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Kubernetes Service'; ResourceType = 'microsoft.containerservice/fleets'; Key = 'UpdateToLatestFleetApi'; Message = 'Update Fleet API'; State = 'Active'; Version = 2; Description = 'Update Fleet API to the latest Version'; LongDescription = 'We have identified sdk calls from outdated Fleet API for resources under this subscription. We recommend switching to the latest sdk versions. This ensures you receive the latest features and performance improvements.'; Benefits = 'Latest Fleet API contain fixes for known issues and additional improvements.'; Tip = ''; LearnMoreLink = ''; }
        ,[PSCustomObject]@{ Id = 'a5e8a0f8-2c84-407a-b3d8-f371d684363b'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'Medium'; ServiceName = 'Azure Fluid Relay Service'; ResourceType = 'microsoft.fluidrelay/fluidrelayservers'; Key = 'UpgradeClientLibrary'; Message = 'Upgrade your Azure Fluid Relay client library'; State = 'Active'; Version = 14; Description = 'Azure Fluid Relay client library should be upgraded'; LongDescription = 'If the Azure Fluid Relay service is invoked with an old client library, it might cause appplication problems. To ensure your application remains operational, upgrade your Azure Fluid Relay client library to the latest version. Upgrading provides the most up-to-date functionality, and enhancements in performance and stability.'; Benefits = 'Improved reliability'; Tip = ''; LearnMoreLink = 'https://learn.microsoft.com/en-us/azure/azure-fluid-relay/concepts/version-compatibility'; }
        ,[PSCustomObject]@{ Id = '8109a740-abe9-453e-91fc-c25598de73d0'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'AccWriteCandidate'; Message = 'Consider upgrading to Accelerated Writes to improve performance of your HBase cluster'; State = 'Active'; Version = 2; Description = 'Consider using Accelerated Writes feature in your HBase cluster to improve cluster performance.'; LongDescription = 'You are seeing this advisor recommendation because HDInsight team''s system log shows that in the past 7 days, your cluster has encountered the following scenarios:\n	1. High WAL sync time latency\n	2. High write request count (at least 3 one hour windows of over 1000 avg_write_requests/second/node)\n\nThese conditions are indicators that your cluster is suffering from high write latencies. This could be due to heavy workload performed on your cluster.\nTo improve the performance of your cluster, you may want to consider utilizing the Accelerated Writes feature provided by Azure HDInsight HBase.  The Accelerated Writes feature for HDInsight Apache HBase clusters attaches premium SSD-managed disks to every RegionServer (worker node) instead of using cloud storage. As a result, provides low write-latency and better resiliency for your applications.\nTo read more on this feature, please visit link: '; Benefits = 'Lower write-latency and better resiliency for your applications.'; Tip = 'Consider upgrading to Accelerated Writes to improve performance of your HBase cluster'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hbase/apache-hbase-accelerated-writes'; }
        ,[PSCustomObject]@{ Id = 'e459ed06-6204-4c85-9f75-9b046b68578a'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'CompactionQueueCandidate'; Message = 'Increasing compaction threads count'; State = 'Active'; Version = 3; Description = 'Consider increasing your compaction threads for compactions to complete faster'; LongDescription = 'The compaction queue in your region servers are more than 2000 suggesting that more data requires compaction. Slower compactions can impact read performance as the number of files to read are more. More files without compaction can also impact the heap usage related to how files interact with Azure file system.'; Benefits = 'Faster compactions would ensure faster reads and less heap usage.'; Tip = 'Increase compaction thread count to improve read performance'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hbase/apache-hbase-advisor'; }
        ,[PSCustomObject]@{ Id = '469b5242-26ee-4a4c-ba65-97479166bcf1'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'FlushQueueCandidate'; Message = 'Tuning the flushes'; State = 'Active'; Version = 3; Description = 'Consider increasing the flusher threads'; LongDescription = 'The flush queue size in your region servers are more than 100 or there are updates getting blocked frequently. Tuning of the flush handler is recommended.'; Benefits = 'Faster flushes would clear the writes from being blocked.'; Tip = 'Increase flush handler count to improve write performance'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hbase/apache-hbase-advisor'; }
        ,[PSCustomObject]@{ Id = 'cd31b398-afa9-40fb-875b-18737857198c'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'GCSCertRotation'; Message = 'Enable critical updates to be applied to your HDInsight clusters'; State = 'Disabled'; Version = 7; Description = 'Enable critical updates to be applied to your HDInsight clusters'; LongDescription = 'HDInsight service is applying an important certificate related update to your cluster. However, one or more policies in your subscription are preventing HDInsight service from creating or modifying network resources (Load balancer, Network Interface and Public IP address) associated with your clusters and applying this update. Please take actions to allow HDInsight service to create or modify network resources (Load balancer, Network interface and Public IP address) associated with your clusters before Jan 13, 2021 05:00 PM UTC. The HDInsight team will be performing updates between Jan 13, 2021 05:00 PM UTC and Jan 16, 2021 05:00 PM UTC. Failure to apply this update may result in your clusters becoming unhealthy and unusable. '; Benefits = 'Ensure cluster health and stability'; Tip = 'Enable critical updates to be applied to your HDInsight clusters'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hdinsight-hadoop-provision-linux-clusters'; }
        ,[PSCustomObject]@{ Id = 'd3a6303c-ac8f-4a9c-aad9-939b440d17ba'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'GCSCertRotationR3DropRecreate'; Message = 'Drop and recreate your HDInsight clusters to apply critical updates'; State = 'Disabled'; Version = 6; Description = 'Drop and recreate your HDInsight clusters to apply critical updates'; LongDescription = 'The HDInsight service has attempted to apply a critical certificate update on all your running clusters. However, due to some custom configuration changes, we are unable to apply the certificate updates on some of your clusters. Please drop and recreate your cluster before Jan 25th, 2021 to prevent the cluster from becoming unhealthy and unusable. '; Benefits = 'Ensure cluster health and stability'; Tip = 'Drop and recreate your HDInsight clusters to apply critical updates'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hdinsight-hadoop-provision-linux-clusters'; }
        ,[PSCustomObject]@{ Id = 'dddd213b-87b9-439e-a607-a9ecd94e0ae1'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'GCSCertRotationR3PlanPatch'; Message = 'Apply critical updates to your HDInsight clusters'; State = 'Disabled'; Version = 5.1; Description = 'Apply critical updates to your HDInsight clusters'; LongDescription = 'The HDInsight service has attempted to apply a critical certificate update on all your running clusters. However, one or more policies in your subscription are preventing HDInsight service from creating or modifying network resources (Load balancer, Network Interface and Public IP address) associated with your clusters and applying this update. Please remove or update your policy assignment to allow HDInsight service to create or modify network resources (Load balancer, Network interface and Public IP address) associated with your clusters before Jan 21, 2021 05:00 PM UTC. The HDInsight team will be performing updates between Jan 21, 2021 05:00 PM UTC and Jan 23, 2021 05:00 PM UTC. To verify the policy update, you can try to create network resources (Load balancer, Network interface and Public IP address) in the same resource group and Subnet where your cluster is in. Failure to apply this update may result in your clusters becoming unhealthy and unusable. You can also drop and recreate your cluster before Jan 25th, 2021 to prevent the cluster from becoming unhealthy and unusable. The HDInsight service will send another notification if we failed to apply the update to your clusters.'; Benefits = 'Ensure cluster health and stability'; Tip = 'Apply critical updates to your HDInsight clusters'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hdinsight-hadoop-provision-linux-clusters'; }
        ,[PSCustomObject]@{ Id = '69740e3e-5b96-4b0e-b9b8-4d7573e3611c'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'GCSCertRotationRound2'; Message = 'Apply critical updates by dropping and recreating your HDInsight clusters (certificate rotation round 2)'; State = 'Active'; Version = 6; Description = 'Apply critical updates by dropping and recreating your HDInsight clusters (certificate rotation round 2)'; LongDescription = 'The HDInsight service attempted to apply a critical certificate update on your running clusters. However, due to some custom configuration changes, we''re unable to apply the updates on all clusters. To prevent those clusters from becoming unhealthy and unusable, drop and recreate your clusters.'; Benefits = 'Ensure cluster health and stability'; Tip = 'Apply critical updates by dropping and recreating your HDInsight clusters (certificate rotation round 2)'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hdinsight-hadoop-provision-linux-clusters'; }
        ,[PSCustomObject]@{ Id = '80c1538d-5962-4b54-8018-1bed379e4029'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'HBaseMemstoreReadPercentage'; Message = 'HBase Query advisor for tuning memstore reads'; State = 'Active'; Version = 2; Description = 'Reads happen on most recent data'; LongDescription = 'More than 75% of your read requests are landing on the memstore. That indicates that the reads are primarily on recent data. This suggests that even if a flush happens on the memstore, the recent file needs to be accessed and that file needs to be in the cache.'; Benefits = 'If the reads are on the most recent data, the suggested config changes will help you do the reads from the memory as much as possible, therefore helping with faster read performance.'; Tip = 'HBase reads tuning'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hbase/apache-hbase-advisor'; }
        ,[PSCustomObject]@{ Id = '36dff9ef-afde-40f5-b742-79a0bafcf6c2'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'KafkaVersionRetirement'; Message = 'Deprecation of Kafka 1.1 in HDInsight 4.0 Kafka cluster'; State = 'Disabled'; Version = 8; Description = 'Deprecation of Kafka 1.1 in HDInsight 4.0 Kafka cluster'; LongDescription = 'Starting July 1, 2020, customers will not be able to create new Kafka clusters with Kafka 1.1 on HDInsight 4.0. Existing clusters will run as is without support from Microsoft. Consider moving to Kafka 2.1 on HDInsight 4.0 by June 30 2020 to avoid potential system/support interruption.'; Benefits = 'Avoid potential system/support interruption'; Tip = 'Deprecation of Kafka 1.1 in HDInsight 4.0 Kafka cluster'; LearnMoreLink = 'https://aka.ms/hdiretirekafka'; }
        ,[PSCustomObject]@{ Id = '353bcb88-3747-4a7a-8b1c-374117db5668'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'PreventVMReboot'; Message = 'Prevent HDInsight cluster VMs from rebooting periodically'; State = 'Disabled'; Version = 6; Description = 'Prevent HDInsight cluster VMs from rebooting periodically.'; LongDescription = 'Starting from mid November 2020, you may have noticed HDInsight cluster VMs getting rebooted on a regular basis. This could be caused by: 1. Clamav is enabled on your cluster and it could consume large amount of memory. Under certain conditions this could trigger node reboot. 2. Microsoft is updating Azure services in a phased manner to use TLS certificates from a different set of Certificate Authorities (CAs). When a new CA certificate is available, HDInsight detects and adds the certificate to the JDK trust store and schedules a reboot in a staggered manner. HDInsight is deploying fixes and applying patch for all running clusters for both issues. To apply the fix immediately and avoid unexpected VMs rebooting, you can run below script actions on all cluster nodes as a persistent script action. HDInsight will post another notice after the fix and patching complete. See recommended actions below to download required scripts.'; Benefits = 'Avoid unexpected VMs rebooting.'; Tip = 'Prevent HDInsight cluster VMs from rebooting periodically'; LearnMoreLink = 'https://docs.microsoft.com/azure/security/fundamentals/tls-certificate-changes'; }
        ,[PSCustomObject]@{ Id = '076f2cce-a86e-4175-adba-4a7456839a47'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'RegionCountCandidate'; Message = 'Region count tuning.'; State = 'Active'; Version = 3; Description = 'Check your region counts as you have blocking updates.'; LongDescription = 'Region counts needs to be adjusted to avoid updates getting blocked. It might require a scale up of the cluster by adding new nodes.'; Benefits = 'Brings more parallelism and ensures writes are more uniform.'; Tip = 'Tune the cluster based on the number of regions'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hbase/apache-hbase-advisor'; }
        ,[PSCustomObject]@{ Id = '3b6f9784-2bf9-4348-a1e6-4554504f213d'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'ScanQueryTuningcandidate'; Message = 'Revisit your scan queries'; State = 'Active'; Version = 2; Description = 'More than 75% of your queries are full scan queries.'; LongDescription = 'More than 75% of the scan queries on your cluster are doing a full region/table scan. Modify your scan queries to avoid full region or table scans.'; Benefits = 'Faster scan performance'; Tip = 'Set start and stop rows to scans'; LearnMoreLink = 'https://docs.microsoft.com/azure/hdinsight/hbase/apache-hbase-advisor'; }
        ,[PSCustomObject]@{ Id = '20dfc768-7850-4176-9707-b9bb52afb95a'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'SparkVersionRetirement'; Message = 'Deprecation of Older Spark Versions in HDInsight Spark cluster'; State = 'Disabled'; Version = 8; Description = 'Deprecation of Older Spark Versions in HDInsight Spark cluster'; LongDescription = 'Starting July 1, 2020, customers will not be able to create new Spark clusters with Spark 2.1 and 2.2 on HDInsight 3.6, and Spark 2.3 on HDInsight 4.0. Existing clusters will run as is without support from Microsoft. '; Benefits = 'Avoid potential system/support interruption.'; Tip = 'Deprecation of Older Spark Versions in HDInsight Spark cluster'; LearnMoreLink = 'https://aka.ms/hdiretirespark'; }
        ,[PSCustomObject]@{ Id = '11741e31-c2ca-4739-92de-04ff7eca697c'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'High'; ServiceName = 'HDInsight'; ResourceType = 'microsoft.hdinsight/clusters'; Key = 'VMDeprecation'; Message = 'Action required: Migrate your A8?A11 HDInsight cluster before 1 March 2021'; State = 'Disabled'; Version = 6; Description = 'Action required: Migrate your A8?A11 HDInsight cluster before 1 March 2021'; LongDescription = 'You''re receiving this notice because you have one or more active A8, A9, A10 or A11 HDInsight cluster. The A8-A11 virtual machines (VMs) will be retired in all regions on 1 March 2021. After that date, all clusters using A8-A11 will be deallocated. Migrate your affected clusters to another HDInsight supported VM (https://azure.microsoft.com/pricing/details/hdinsight/) before that date. For more details, see ''Learn More'' link or contact us at askhdinsight@microsoft.com'; Benefits = 'Avoid cluster getting deallocated.'; Tip = 'Action required: Migrate your A8?A11 HDInsight cluster before 1 March 2021'; LearnMoreLink = 'https://azure.microsoft.com/updates/a8-a11-azure-virtual-machine-sizes-will-be-retired-on-march-1-2021/'; }
        ,[PSCustomObject]@{ Id = '06c03895-e210-4153-a4a0-a5e5c8e8eb83'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Virtual Machines'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'UpgradeSizeHighVMUtilV0'; Message = 'Upgrade the size of your most active virtual machines to prevent resource exhaustion and improve performance'; State = 'Active'; Version = 0; Description = 'Upgrade the size of your most active virtual machines to prevent resource exhaustion and improve performance'; LongDescription = 'We analyzed data for the past 7 days and identified virtual machines (VMs) with high utilization across different metrics (i.e., CPU, Memory, and VM IO). Those VMs may experience performance issues since they are nearing/at their SKU''s limits. Consider upgrading their SKU to improve performance.'; Benefits = 'Improve the performance and reliability of your VMs'; Tip = 'You can improve the performance and reliability of your highly utilized virtual machines'; LearnMoreLink = 'https://aka.ms/aa_resizehighusagevmrec_learnmore'; }
        ,[PSCustomObject]@{ Id = '9d5717d2-4708-4e3f-bdda-93b3e6f1715b'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Azure Arc enabled Servers'; ResourceType = 'microsoft.hybridcompute/machines'; Key = 'ArcServerAgentVersion'; Message = 'Upgrade the Azure Connected Machine agent'; State = 'Active'; Version = 8; Description = 'Upgrade to the latest version of the Azure Connected Machine agent'; LongDescription = 'The Azure Connected Machine agent is updated regularly with bug fixes, stability enhancements, and new functionality. For the best Azure Arc experience, upgrade your agent to the latest version.'; Benefits = 'Improved stability and new functionality'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/azure-arc/servers/manage-agent'; }
        ,[PSCustomObject]@{ Id = '03e77a09-fc67-4bb6-86ed-42bda42fb9ad'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Monitor'; ResourceType = 'microsoft.insights/scheduledqueryrules'; Key = 'ScheduledQueryRulesRp'; Message = 'Log alert rule was disabled'; State = 'Active'; Version = 3; Description = 'Log alert rule was disabled'; LongDescription = 'The alert rule was disabled by Azure Monitor as it was causing service issues. To enable the alert rule, contact support.'; Benefits = 'Ensure continued monitoring and alerting for your resources'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_logalerts_queryrepair'; }
        ,[PSCustomObject]@{ Id = '2b5eac39-9f50-4d8d-bc9b-1e1e07c5c37e'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Monitor'; ResourceType = 'microsoft.insights/scheduledqueryrules'; Key = 'ScheduledQueryRulesLogAlert'; Message = 'Repair log alert rule'; State = 'Active'; Version = 3; Description = 'Repair your log alert rule'; LongDescription = 'We have detected that one or more of your alert rules have invalid queries specified in their condition section. Log alert rules are created in Azure Monitor and are used to run analytics queries at specified intervals. The results of the query determine if an alert needs to be triggered. Analytics queries may become invalid overtime due to changes in referenced resources, tables, or commands. We recommend that you correct the query in the alert rule to prevent it from getting auto-disabled and ensure monitoring coverage of your resources in Azure.'; Benefits = 'Ensure continued monitoring and alerting for your resources'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_logalerts_queryrepair'; }
        ,[PSCustomObject]@{ Id = '51b1fad8-4838-426f-9871-107bc089677b'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'Medium'; ServiceName = 'Iot Hub'; ResourceType = 'microsoft.devices/iothubs'; Key = 'UpgradeEdgeSdk'; Message = 'Upgrade Microsoft Edge device runtime to a supported version for IoT Hub'; State = 'Active'; Version = 7; Description = 'Upgrade Microsoft Edge device runtime to a supported version for IoT Hub'; LongDescription = 'When Edge devices use outdated versions, performance degradation might occur. We recommend you upgrade to the latest supported version of the Azure IoT Edge runtime.'; Benefits = 'Ensure business continuity with latest supported version for your Edge devices'; Tip = ''; LearnMoreLink = 'https://aka.ms/IOTEdgeSDKCheck'; }
        ,[PSCustomObject]@{ Id = 'd448c687-b808-4143-bbdc-02c35478198a'; Category = 'High Availability'; Control = 'Service Upgrade and Retirement'; Impact = 'Medium'; ServiceName = 'Iot Hub'; ResourceType = 'microsoft.devices/iothubs'; Key = 'UpgradeDeviceClientSdk'; Message = 'Upgrade Device Client SDK for IotHub'; State = 'Active'; Version = 7.6; Description = 'Upgrade device client SDK to a supported version for IotHub'; LongDescription = 'When devices use an outdated SDK, performance degradation can occur. Some or all of your devices are using an outdated SDK. We recommend you upgrade to a supported SDK version.'; Benefits = 'Ensure business continuity with supported SDK for your devices'; Tip = ''; LearnMoreLink = 'https://aka.ms/iothubsdk'; }
        ,[PSCustomObject]@{ Id = '9017e82f-b7ac-4a06-8b9b-5858cb3d5113'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'KeyVault'; ResourceType = 'microsoft.keyvault/vaults'; Key = 'UpgradeKeyVaultSDK'; Message = 'Update Key Vault Library'; State = 'Active'; Version = 1; Description = 'Update Key Vault SDK Version'; LongDescription = 'New Key Vault Client Libraries are split to keys, secrets, and certificates SDKs, which are integrated with recommended Azure Identity library to provide seamless authentication to Key Vault across all languages and environments. It also contains several performance fixes to issues reported by customers and proactively identified through our QA process.<br><br>**PLEASE DISMISS:**<br>If Key Vault is integrated with Azure Storage, Disk or other Azure services which can use old Key Vault SDK and when all your current custom applications are using .NET SDK 4.0 or above.'; Benefits = 'Latest Key Vault Client Libraries contain fixes for known issues and additional improvements.'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/key-vault/general/client-libraries'; }
        ,[PSCustomObject]@{ Id = '47e36ece-24bb-4d3e-8172-af28c9df172d'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Key Vault'; ResourceType = 'microsoft.keyvault/managedhsms'; Key = 'UpgradeKeyVaultMHSMSDK'; Message = 'Update Key Vault Library'; State = 'Active'; Version = 2; Description = 'Update Key Vault SDK Version'; LongDescription = 'New Key Vault Client Libraries are split to keys, secrets, and certificates SDKs, which are integrated with recommended Azure Identity library to provide seamless authentication to Key Vault across all languages and environments. It also contains several performance fixes to issues reported by customers and proactively identified through our QA process.	Important: Please be aware that you can only remediate recommendation for custom applications you have access to. Recommendations can be shown due to integration with other Azure services like Storage, Disk encryption, which are in process to update to new version of our SDK. If you use .NET 4.0 in all your applications please dismiss'; Benefits = 'Latest Key Vault Client Libraries contain fixes for known issues and additional improvements.'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/key-vault/general/client-libraries'; }
        ,[PSCustomObject]@{ Id = '3dbfbe10-4a27-4d10-9369-256e99f83a9c'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Language Service'; ResourceType = 'microsoft.cognitiveservices/accounts'; Key = 'UpgradeToLatestAPILanguage'; Message = 'Upgrade to the latest API version of Azure Cognitive Service for Language'; State = 'Active'; Version = 4; Description = 'Upgrade to the latest API version of Azure Cognitive Service for Language'; LongDescription = 'Please upgrade to the latest API version to get the best results in terms of model quality, performance and service availability.'; Benefits = 'Better service availability and performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/language-api'; }
        ,[PSCustomObject]@{ Id = '3f319edd-fdce-4ab5-8f6d-c63dc4cea73b'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Language'; ResourceType = 'microsoft.cognitiveservices/accounts'; Key = 'UpgradeToLatestSDKLanguage'; Message = 'Upgrade to the latest SDK version for Language'; State = 'Active'; Version = 4; Description = 'Upgrade to the latest Cognitive Service Language SDK version'; LongDescription = 'Please upgrade to the latest SDK version to get the best results in terms of model quality, performance and service availability.'; Benefits = 'Better service availability and performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/language-api'; }
        ,[PSCustomObject]@{ Id = '94aea435-ef39-493f-a547-8408092c22a7'; Category = 'Cost'; Control = ''; Impact = 'High'; ServiceName = 'Virtual Machine Scale Sets'; ResourceType = 'microsoft.compute/virtualmachinescalesets'; Key = 'LowUsageVmss'; Message = 'Shut down or resize your virtual machine scale sets'; State = 'Active'; Version = 9.9; Description = 'Right-size or shutdown underutilized virtual machine scale sets'; LongDescription = 'We''ve analyzed the usage patterns of your virtual machine scale sets over the past 7 days and identified virtual machine scale sets with low usage. While certain scenarios can result in low utilization by design, you can often save money by managing the size and number of virtual machine scale sets.'; Benefits = 'savings'; Tip = 'You can optimize underutilized virtual machine scale sets to reduce your monthly Azure spend.'; LearnMoreLink = 'https://aka.ms/aa_lowusagerec_vmss_learnmore'; }
        ,[PSCustomObject]@{ Id = 'e10b1381-5f0a-47ff-8c7b-37bd13d7c974'; Category = 'Cost'; Control = ''; Impact = 'High'; ServiceName = 'Virtual Machines'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'LowUsageVmV2'; Message = 'Shut down or resize your virtual machine'; State = 'Active'; Version = 8.4; Description = 'Right-size or shutdown underutilized virtual machines'; LongDescription = 'We''ve analyzed the usage patterns of your virtual machine and identified virtual machines with low usage. While certain scenarios can result in low utilization by design, you can often save money by managing the size and number of virtual machines.'; Benefits = 'savings'; Tip = 'You can optimize underutilized virtual machines to reduce your monthly Azure spend.'; LearnMoreLink = 'https://aka.ms/aa_lowusagerec_learnmore'; }
        ,[PSCustomObject]@{ Id = '36c3633b-daac-4e01-af95-11b8c2f4fe20'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'ManagedDisksStorageAccount'; Message = 'Use Managed Disks'; State = 'Active'; Version = 2; Description = 'Use Managed disks to prevent disk I/O throttling'; LongDescription = 'Your virtual machine disks belong to a storage account that has reached its scalability target, and is susceptible to I/O throttling. To protect your virtual machine from performance degradation and to simplify storage management, use Managed Disks.'; Benefits = 'Improved data resilience and performance'; Tip = 'You can use Managed Disks to prevent disk throttling and improve virtual machine performance.'; LearnMoreLink = 'https://aka.ms/aa_avset_manageddisk_learnmore'; }
        ,[PSCustomObject]@{ Id = '1e6ead2a-8ebc-4381-8c99-e2937ae32700'; Category = 'Operational Excellence'; Control = ''; Impact = 'Low'; ServiceName = 'Azure Lighthouse'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'OnboardCSPSubscriptionsToLighthouse'; Message = 'Use Azure Lighthouse to simply and securely manage customer subscriptions at scale'; State = 'Active'; Version = 1; Description = 'Use Azure Lighthouse to simply and securely manage customer subscriptions at scale'; LongDescription = 'Using Azure Lighthouse improves security and reduces unnecessary access to your customer tenants by enabling more granular permissions for your users. It also allows for greater scalability, as your users can work across multiple customer subscriptions using a single login in your tenant.'; Benefits = 'Scalable, secure, and simplified management experience'; Tip = 'You can use Azure Lighthouse to manage your customers with granular access to customer resources.'; LearnMoreLink = 'https://docs.microsoft.com/azure/lighthouse/concepts/cloud-solution-provider'; }
        ,[PSCustomObject]@{ Id = 'b7c9fd99-a979-40b4-ab48-b1dfab6bb41a'; Category = 'High Availability'; Control = 'Scalability '; Impact = 'Medium'; ServiceName = 'Media Services'; ResourceType = 'microsoft.media/mediaservices'; Key = 'AccountQuotaLimit'; Message = 'Increase Media Services quotas or limits'; State = 'Active'; Version = 7; Description = 'Increase Media Services quotas or limits'; LongDescription = 'When a media account hits its quota limits, disruption of service might occur. To avoid any disruption of service, review current usage of assets, content key policies, and stream policies and increase quota limits for the entities that are close to hitting the limit. You can request quota limits be increased by opening a ticket and adding relevant details. TIP: Don''t create additional Azure Media accounts in an attempt to obtain higher limits.'; Benefits = 'Avoid any disruption to service due to customer exceeding quota limits.'; Tip = ''; LearnMoreLink = 'https://aka.ms/ams-quota-recommendation/'; }
        ,[PSCustomObject]@{ Id = 'e47b63ff-82bb-4350-a806-706085980c27'; Category = 'Cost'; Control = ''; Impact = 'Low'; ServiceName = 'CloudES_AzureAdvisor'; ResourceType = 'microsoft.network/trafficmanagerprofiles'; Key = 'AdvisorMockRecommendationType'; Message = 'Mock Recommendation for Advisor'; State = 'Active'; Version = 1.1; Description = 'Mock recommendation type for Advisor'; LongDescription = 'Mock recommendation type'; Benefits = 'Mock Recommendation'; Tip = ''; LearnMoreLink = 'https://aka.ms/advisordocs'; }
