        ,[PSCustomObject]@{ Id = '6af349c1-a2a5-4021-999a-4b8503c50aa2'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'CloudES_AzureAdvisor'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'KustoMockRecommendationType'; Message = 'KustoMockRecommendationType'; State = 'Active'; Version = 4.2; Description = 'Mock recommendation type for Kusto-based ingestion'; LongDescription = 'Mock recommendation type for Kusto-based ingestion.'; Benefits = 'KustoMockRecommendationType'; Tip = ''; LearnMoreLink = 'https://aka.ms/advisordocs'; }
