        ,[PSCustomObject]@{ Id = '6a2b1e70-bd4c-4163-86de-5243d7ac05ee'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'Application Gateway'; ResourceType = 'microsoft.network/applicationgateways'; Key = 'AppGateway'; Message = 'Upgrade gateway size or add more instances'; State = 'Active'; Version = 7; Description = 'Upgrade your SKU or add more instances'; LongDescription = 'Deploying two or more medium or large sized instances ensures business continuity (fault tolerance) during outages caused by planned or unplanned maintenance.'; Benefits = 'Ensure business continuity through application gateway resilience'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_gatewayrec_learnmore'; }
        ,[PSCustomObject]@{ Id = '52a9d0a7-efe1-4512-9716-394abd4e0ab1'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Application Gateway'; ResourceType = 'microsoft.network/applicationgateways'; Key = 'AppGatewayHostOverride'; Message = 'Do not override hostname to ensure website integrity'; State = 'Active'; Version = 12; Description = 'Avoid hostname override to ensure site integrity'; LongDescription = 'Avoid overriding the hostname when configuring Application Gateway. Having a domain on the frontend of Application Gateway different than the one used to access the backend, can lead to broken cookies or redirect URLs. Make sure the backend is able to deal with the domain difference, or update the Application Gateway configuration so the hostname doesn''t need to be overwritten towards the backend. When used with App Service, attach a custom domain name to the Web App and avoid use of the *.azurewebsites.net host name towards the backend. Note that a different frontend domain isn''t a problem in all situations, and certain categories of backends like REST APIs, are less sensitive in general.'; Benefits = 'Ensure site integrity and avoid broken cookies or redirect urls through a resilient Application Gateway configuration.'; Tip = ''; LearnMoreLink = 'https://aka.ms/appgw-advisor-usecustomdomain'; }
        ,[PSCustomObject]@{ Id = '2ee9f31e-df58-4893-b3e7-66c0cd74183a'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Hot Application Gateway'; ResourceType = 'microsoft.network/applicationgateways'; Key = 'HotAppGateway'; Message = 'Properly scale your Application Gateway'; State = 'Active'; Version = 4; Description = 'Make sure you have enough instances in your Application Gateway to support your traffic'; LongDescription = 'Your Application Gateway has been running on high utilization recently and under heavy load, you may experience traffic loss or increase in latency. It is important that you scale your Application Gateway according to your traffic and with a bit of a buffer so that you are prepared for any traffic surges or spikes and minimizing the impact that it may have in your QoS. Application Gateway v1 SKU (Standard/WAF) supports manual scaling and v2 SKU (Standard_v2/WAF_v2) support manual and autoscaling. In case of manual scaling, increase your instance count and if autoscaling is enabled, make sure your maximum instance count is set to a higher value so Application Gateway can scale out as the traffic increases'; Benefits = 'Ensure availability of your sites'; Tip = 'Properly scale your Application Gateway'; LearnMoreLink = 'https://aka.ms/hotappgw'; }
        ,[PSCustomObject]@{ Id = '17ebccd8-1405-405c-8695-1981d115ffdc'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Bastion'; ResourceType = 'microsoft.network/bastionhosts'; Key = 'RecreateBastionResource'; Message = 'Delete and recreate your Azure Bastion resource before February 19, 2021'; State = 'Active'; Version = 2; Description = 'Delete and recreate your Azure Bastion resource before February 19, 2021'; LongDescription = 'We are unable to update your Azure Bastion resource due to its current configuration. Please delete and recreate your resource before February 19, 2021 to receive the updates. If you do not delete and recreate your resource by this date, it will automatically be deleted and recreated for you.'; Benefits = 'Receive necessary updates for your Azure Bastion resource.'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/bastion/tutorial-create-host-portal'; }
        ,[PSCustomObject]@{ Id = 'da6630fb-4286-4996-92a3-a43f5f26dd34'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'ExpressRoute'; ResourceType = 'microsoft.network/expressroutecircuits'; Key = 'ExpressRouteCircuit'; Message = 'Delete ExpressRoute circuit in Not Provisioned state'; State = 'Disabled'; Version = 3; Description = 'Delete ExpressRoute circuits in the provider status of Not Provisioned'; LongDescription = 'We noticed that your ExpressRoute circuit is in the provider status of Not Provisioned for more than one month. This circuit is currently billed hourly to your subscription. We recommend that you delete the circuit if you aren''t planning to provision the circuit with your connectivity provider.'; Benefits = 'Cost savings'; Tip = ''; LearnMoreLink = 'https://aka.ms/expressroute'; }
        ,[PSCustomObject]@{ Id = '643be269-d022-4185-b945-66837496a879'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'ExpressRoute'; ResourceType = 'microsoft.network/expressroutecircuits'; Key = 'UseGlobalReachForDR'; Message = 'Use Global Reach for disaster recovery'; State = 'Disabled'; Version = 7; Description = 'Use ExpressRoute Global Reach to improve your design for disaster recovery'; LongDescription = 'Unconnected ExpressRoute circuits peered in at least two different locations can cause traffic problems. To allow traffic to continue flowing between your on-premises network and Azure environments if one circuit loses connectivity, connect the circuits to each other using ExpressRoute Global Reach. You can establish Global Reach connections between circuits in different peering locations within the same metro or across metros.'; Benefits = 'Implement a disaster recovery design and maintain connectivity between your on-premises networks and Azure in the event of a failure'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/expressroute/about-upgrade-circuit-bandwidth'; }
        ,[PSCustomObject]@{ Id = '17454550-1543-4068-bdaf-f3ed7cdd3d86'; Category = 'High Availability'; Control = 'Monitoring and Alerting'; Impact = 'Medium'; ServiceName = 'ExpressRoute'; ResourceType = 'microsoft.network/expressroutecircuits'; Key = 'ExpressRouteGatewayE2EMonitoring'; Message = 'Implement ExpressRoute Monitor on Network Performance Monitor for end-to-end monitoring of your ExpressRoute circuit'; State = 'Active'; Version = 6; Description = 'Implement ExpressRoute Monitor on Network Performance Monitor'; LongDescription = 'When ExpressRoute circuit isn''t monitored by ExpressRoute Monitor on Network Performance, you miss notifications of loss, latency, and performance of on-premises to Azure resources, and Azure to on-premises resources. For end-to-end monitoring, implement ExpressRoute Monitor on Network Performance.'; Benefits = 'Improve time-to-detect and time-to-mitigate issues in your network and provide insights on your network path via ExpressRoute'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/expressroute/how-to-npm'; }
        ,[PSCustomObject]@{ Id = 'f606607c-ee34-445e-997e-49d7cb563fe0'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'ExpressRoute'; ResourceType = 'microsoft.network/expressroutecircuits'; Key = 'UpgradeERCircuitBandwidth'; Message = 'Upgrade your ExpressRoute circuit bandwidth'; State = 'Active'; Version = 1; Description = 'Upgrade your ExpressRoute circuit bandwidth to accommodate your bandwidth needs'; LongDescription = 'You have been using over 90% of your procured circuit bandwidth recently. If you exceed your allocated bandwidth, you will experience an increase in dropped packets sent over ExpressRoute. Upgrade your circuit bandwidth to maintain performance if your bandwidth needs remain this high.'; Benefits = 'Prevent packet drops caused by bandwidth oversubscription'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/expressroute/about-upgrade-circuit-bandwidth'; }
        ,[PSCustomObject]@{ Id = '70f87e66-9b2d-4bfa-ae38-1d7d74837689'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'ExpressRoute'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'ExpressRouteGatewayRedundancy'; Message = 'Implement multiple ExpressRoute circuits in your Virtual Network for cross premises resiliency'; State = 'Active'; Version = 6; Description = 'Implement multiple ExpressRoute circuits in your Virtual Network for cross premises resiliency'; LongDescription = 'When an ExpressRoute gateway only has one ExpressRoute circuit associated to it, resiliency issues might occur. To ensure peering location redundancy and resiliency, connect one or more additional circuits to your gateway.'; Benefits = 'Improve resiliency in case of ExpressRoute peering location failure'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/expressroute/designing-for-high-availability-with-expressroute'; }
        ,[PSCustomObject]@{ Id = 'b020ff96-37bf-4a64-8bd5-2bfb3fdf3f87'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Traffic Manager'; ResourceType = 'microsoft.network/trafficmanagerprofiles'; Key = 'FastFailOverTTL'; Message = 'Configure Time to Live'; State = 'Active'; Version = 2; Description = 'Configure DNS Time to Live to 20 seconds'; LongDescription = 'Time to Live (TTL) affects how recent of a response a client will get when it makes a request to Azure Traffic Manager. Reducing the TTL value means that the client will be routed to a functioning endpoint faster in the case of a failover. Configure your TTL to 20 seconds to route traffic to a health endpoint as quickly as possible.'; Benefits = 'Improve availability by failing over to healthy endpoints'; Tip = ''; LearnMoreLink = 'https://aka.ms/Ngfw4r'; }
        ,[PSCustomObject]@{ Id = '6cd70072-c45c-4716-bf7b-b35c18e46e72'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'Traffic Manager'; ResourceType = 'microsoft.network/trafficmanagerprofiles'; Key = 'GeneralProfile'; Message = 'Add Endpoint'; State = 'Active'; Version = 8; Description = 'Add at least one more endpoint to the profile, preferably in another Azure region'; LongDescription = 'Profiles need more than one endpoint to ensure availability if one of the endpoints fails. We also recommend that endpoints be in different regions.'; Benefits = 'Improve resiliency by allowing failover'; Tip = ''; LearnMoreLink = 'https://aka.ms/AA1o0x4'; }
        ,[PSCustomObject]@{ Id = '0bbe0a49-3c63-49d3-ab4a-aa24198f03f7'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'High'; ServiceName = 'Traffic Manager'; ResourceType = 'microsoft.network/trafficmanagerprofiles'; Key = 'GeographicProfile'; Message = 'Add Endpoint'; State = 'Active'; Version = 6; Description = 'Add an endpoint configured to "All (World)"'; LongDescription = 'For geographic routing, traffic is routed to endpoints in defined regions. When a region fails, there is no pre-defined failover. Having an endpoint where the Regional Grouping is configured to "All (World)" for geographic profiles avoids traffic black holing and guarantees service availablity.'; Benefits = 'Improve resiliency by avoiding traffic black holes'; Tip = ''; LearnMoreLink = 'https://aka.ms/Rf7vc5'; }
        ,[PSCustomObject]@{ Id = 'd374a732-e69b-41dc-bbc2-a7234e2270be'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Traffic Manager'; ResourceType = 'microsoft.network/trafficmanagerprofiles'; Key = 'ProfileTTL'; Message = 'Configure Time to Live'; State = 'Active'; Version = 4; Description = 'Configure DNS Time to Live to 60 seconds'; LongDescription = 'Time to Live (TTL) affects how recent of a response a client will get when it makes a request to Azure Traffic Manager. Reducing the TTL value means that the client will be routed to a functioning endpoint faster in the case of a failover. Configure your TTL to 60 seconds to route traffic to a health endpoint as quickly as possible.'; Benefits = 'Improve availability by failing over to healthy endpoints faster'; Tip = ''; LearnMoreLink = 'https://aka.ms/Um3xr5'; }
        ,[PSCustomObject]@{ Id = '0db76759-6d22-4262-93f0-2f989ba2b58e'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'Traffic Manager'; ResourceType = 'microsoft.network/trafficmanagerprofiles'; Key = 'ProximityProfile'; Message = 'Add or Move Endpoint'; State = 'Active'; Version = 5; Description = 'Add or move one endpoint to another Azure region'; LongDescription = 'All endpoints associated to this proximity profile are in the same region. Users from other regions may experience long latency when attempting to connect. Adding or moving an endpoint to another region will improve overall performance for proximity routing and provide better availability if all endpoints in one region fail.'; Benefits = 'Improve resiliency by allowing failover to another region'; Tip = ''; LearnMoreLink = 'https://aka.ms/Ldkkdb'; }
        ,[PSCustomObject]@{ Id = '43a3c88f-5bef-46c8-8e17-932159b3287d'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'AristaNVAAccelNet'; Message = 'Accelerated Networking Recommendation for Arista Networks vEOS Router'; State = 'Disabled'; Version = 1; Description = 'Arista Networks vEOS Router may experience high CPU utilization, reduced throughput and high latency.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of Arista Networks vEOS Router Image that is running older drivers for Accelerated Networking, which may cause the product to revert to using the standard, synthetic network interface which does not use Accelerated Networking. It is recommended that you upgrade to a newer version of the image that addresses this issue and enable Accelerated Networking. Please contact Arista Networks for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'You will not experience an unexpected degradation in network performance.'; Tip = 'Upgrade Arista Networks vEOS Router image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = '1f39d16f-68b9-472b-a82d-cf7b5bed0430'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'AristaVeosANUpgradeRecommendation'; Message = 'Upgrade your Network Virtual Appliance Version to enable Accelerated Networking on all platforms.'; State = 'Disabled'; Version = 1; Description = 'Update to the latest version of your Arista VEOS product for Accelerated Networking support.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of software image that is running older drivers for Accelerated Networking (AN). It has a synthetic network interface which, either, is not AN capable or is not compatible with all Azure hardware. It is recommended that you upgrade to the latest version of the image that addresses this issue and enable Accelerated Networking. Please contact your vendor for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'Faster network throughput with lower latency.'; Tip = 'Upgrade your Network Virtual Appliance image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = '270c5ab3-31d0-461f-bf8d-55d739552d31'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'BarracudaNVAAccelNet'; Message = 'Accelerated Networking Recommendation for Barracuda Networks NextGen Firewall'; State = 'Disabled'; Version = 1; Description = 'Barracuda Networks NextGen Firewall may experience high CPU utilization, reduced throughput and high latency.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of Barracuda Networks NextGen Firewall Image that is running older drivers for Accelerated Networking, which may cause the product to revert to using the standard, synthetic network interface which does not use Accelerated Networking. It is recommended that you upgrade to a newer version of the image that addresses this issue and enable Accelerated Networking. Please contact Barracuda Networks for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'You will not experience an unexpected degradation in network performance.'; Tip = 'Upgrade Barracuda Networks NextGen Firewall image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = 'f892f705-a708-47b0-9de2-95a958c479c2'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'BarracudaNgANUpgradeRecommendation'; Message = 'Upgrade your Network Virtual Appliance Version to enable Accelerated Networking on all platforms.'; State = 'Disabled'; Version = 1; Description = 'Update to the latest version of your Barracuda NG Firewall product for Accelerated Networking support.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of software image that is running older drivers for Accelerated Networking (AN). It has a synthetic network interface which, either, is not AN capable or is not compatible with all Azure hardware. It is recommended that you upgrade to the latest version of the image that addresses this issue and enable Accelerated Networking. Please contact your vendor for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'Faster network throughput with lower latency.'; Tip = 'Upgrade your Network Virtual Appliance image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = 'd6b4832b-5ad3-454c-ac5f-1543bd1462dc'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'CheckPointCGANUpgradeRecommendation'; Message = 'Upgrade your Network Virtual Appliance Version to enable Accelerated Networking on all platforms.'; State = 'Disabled'; Version = 2.2; Description = 'Update to the latest version of your Check Point product for Accelerated Networking support.'; LongDescription = 'We have identified that your Virtual Machine (VM) might be running a version of software image that is running older drivers for Accelerated Networking (AN). Your VM has a synthetic network interface that is either not AN capable or is not compatible with all Azure hardware. We recommend that you upgrade to the latest version of the image that addresses this issue and enable Accelerated Networking. Please contact your vendor for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'Faster network throughput with lower latency.'; Tip = 'Upgrade your Network Virtual Appliance image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = '6230537d-113a-42ec-9357-c08d01602787'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'CheckPointPlatformServicingKnownIssueA'; Message = 'Known Issue with Check Point Network Virtual Appliance image version'; State = 'Disabled'; Version = 5; Description = 'Check Point Virtual Machine may lose Network Connectivity.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of Check Point image that has been known to lose network connectivity in the event of a platform servicing operation. It is recommended that you upgrade to a newer version of the image that addresses this issue. Please contact Check Point for further instructions on how to upgrade your image.'; Benefits = 'Ensure business continuity through better network connectivity.'; Tip = 'Upgrade Network Virtual Appliance image to ensure business continuity.'; LearnMoreLink = 'https://supportcenter.checkpoint.com/supportcenter/portal?eventSubmit_doGoviewsolutiondetails=&solutionid=sk151752&partition=Advanced&product=CloudGuard'; }
