        ,[PSCustomObject]@{ Id = 'a71bfb94-b9e8-473e-b0a5-9fafc92cdb05'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'GetF5vulnK03009991'; Message = 'NVA Image no longer suppported and not available in Market Place'; State = 'Disabled'; Version = 1; Description = 'The iControl REST interface has an unauthenticated remote command execution vulnerability.'; LongDescription = 'This vulnerability allows for unauthenticated attackers with network access to the iControl REST interface, through the BIG-IP management interface and self IP addresses, to execute arbitrary system commands, create or delete files, and disable services. This vulnerability can only be exploited through the control plane and cannot be exploited through the data plane. Exploitation can lead to complete system compromise. The BIG-IP system in Appliance mode is also vulnerable'; Benefits = 'Avoid security incidents and unauthorized access to VM.'; Tip = 'Please review the information in the link '; LearnMoreLink = 'https://support.f5.com/csp/article/K03009991'; }
        ,[PSCustomObject]@{ Id = '148fec38-cb1b-46ad-84b9-41233d07d4d9'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'GetVmListANDisabled'; Message = 'NVA Accellerated Networking enabled but not working'; State = 'Disabled'; Version = 1; Description = 'NVA Accelerated Networking enabled but potentially not working.'; LongDescription = 'Desired state for Accelerated Networking is set to ?true'' for one or more interfaces on this VM, but actual state for accelerated networking is not enabled.'; Benefits = 'Accelerated Networking offers improvements in network performance and reduced latency.'; Tip = 'Please review the information in the link'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli'; }
