        ,[PSCustomObject]@{ Id = '4dce8273-2f9d-4c20-91ab-9485b03b7d10'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'NetAppANUpgradeRecommendation'; Message = 'Upgrade your Network Virtual Appliance Version to enable Accelerated Networking on all platforms.'; State = 'Disabled'; Version = 1; Description = 'Update to the latest version of your NetApp product for Accelerated Networking support.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of software image that is running older drivers for Accelerated Networking (AN). It has a synthetic network interface which, either, is not AN capable or is not compatible with all Azure hardware. It is recommended that you upgrade to the latest version of the image that addresses this issue and enable Accelerated Networking. Please contact your vendor for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'Faster network throughput with lower latency.'; Tip = 'Upgrade your Network Virtual Appliance image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = '010692cc-0668-43fa-b7dc-6766efb22e59'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'NVA'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'NVAHighCPU'; Message = 'NVA with high CPU'; State = 'Disabled'; Version = 1; Description = 'Consider increasing the size of your NVA to address persistent high CPU'; LongDescription = 'When NVAs run at high CPU, packets can get dropped resulting in connection failures or high latency due to network retransmits. Your NVA is running at high CPU, so you should consider increasing the VM size as allowed by the NVA vendor''s licensing requirements.'; Benefits = 'Reduce the odds of packet drops'; Tip = ''; LearnMoreLink = 'https://aka.ms/NVAHighCPU'; }
        ,[PSCustomObject]@{ Id = 'f266acba-699d-4900-ae93-1bb488fd69df'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'NvaMaxFlowLimit'; Message = 'NVA VM Instance Maximum Flow Limit'; State = 'Disabled'; Version = 1.1; Description = 'NVA may see traffic loss due to hitting the maximum number of flows.'; LongDescription = 'Packet loss has been observed for this Virtual Machine due to hitting or exceeding the maximum number of flows for a VM instance of this size on Azure'; Benefits = 'You will see increased performance'; Tip = 'Scale to use more VMs or increase the VM size to more than 8 cores'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/virtual-machine-network-throughput'; }
        ,[PSCustomObject]@{ Id = 'e9a96e96-c68f-4604-b9ff-6b57ffb693da'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'PaloAltoNVAAccelNet'; Message = 'Accelerated Networking Recommendation for Palo Alto Networks VM-Series Firewall'; State = 'Disabled'; Version = 1; Description = 'Palo Alto Networks VM-Series Firewall may experience high CPU utilization, reduced throughput and high latency.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of Palo Alto Networks VM-Series Firewall Image that is running older drivers for Accelerated Networking, which may cause the product to revert to using the standard, synthetic network interface which does not use Accelerated Networking. It is recommended that you upgrade to a newer version of the image that addresses this issue and enable Accelerated Networking. Please contact Palo Alto Networks for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'You will not experience an unexpected degradation in network performance.'; Tip = 'Upgrade Palo Alto Networks VM-Series Firewall image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = '1964f651-15e7-4519-bd86-9a7c5335bfe4'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'NVA Engineering'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'PaloAltoFWANUpgradeRecommendation'; Message = 'Upgrade your Network Virtual Appliance Version to enable Accelerated Networking on all platforms.'; State = 'Disabled'; Version = 1; Description = 'Update to the latest version of your Palo Alto Firewall product for Accelerated Networking support.'; LongDescription = 'We have identified that your Virtual Machine might be running a version of software image that is running older drivers for Accelerated Networking (AN). It has a synthetic network interface which, either, is not AN capable or is not compatible with all Azure hardware. It is recommended that you upgrade to the latest version of the image that addresses this issue and enable Accelerated Networking. Please contact your vendor for further instructions on how to upgrade your Network Virtual Appliance Image.'; Benefits = 'Faster network throughput with lower latency.'; Tip = 'Upgrade your Network Virtual Appliance image version to eliminate any unexpected degradation in network performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-network/create-vm-accelerated-networking-cli#enable-accelerated-networking-on-existing-vms'; }
        ,[PSCustomObject]@{ Id = 'f3be5a9b-2583-4a1c-b774-2481c00ccb04'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'VPN Gateway'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'ActiveActiveGatewaySingleTunnel'; Message = 'Configure redundant tunnel'; State = 'Disabled'; Version = 5; Description = 'Configure redundant tunnel for your active-active VPN gateway'; LongDescription = 'In active-active configuration, each Azure gateway instance has a unique public IP address, and each establishes an IPsec/IKE S2S VPN tunnel to your on-premises VPN device specified in your local network gateway and connection. Note that both VPN tunnels are actually part of the same connection. You need to configure your on-premises VPN device to accept or establish two S2S VPN tunnels to those two Azure VPN gateway public IP addresses.'; Benefits = 'Improve availability and aggregate throughput'; Tip = 'You can configure redundant tunnels for your active-active vpn gateway to improve availability.'; LearnMoreLink = 'https://aka.ms/aa_activeactive_singletun_learnmore'; }
        ,[PSCustomObject]@{ Id = 'e070c4bf-afaf-413e-bc00-e476b89c5f3d'; Category = 'High Availability'; Control = 'High Availability'; Impact = 'Medium'; ServiceName = 'VPN Gateway'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'BasicVPNGateway'; Message = 'Move to production gateway SKUs from Basic gateways'; State = 'Active'; Version = 6; Description = 'Move to production gateway SKUs from Basic gateways'; LongDescription = 'The Basic VPN SKU is for development or testing scenarios. If you''re using the VPN gateway for production, move to a production SKU, which offers higher numbers of tunnels, Border Gateway Protocol (BGP), active-active configuration, custom IPsec/IKE policy, and increased stability and availability.'; Benefits = 'Additional available features and higher stability and availability'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_basicvpngateway_learnmore'; }
        ,[PSCustomObject]@{ Id = '129d8c1e-a4d2-4bac-86ce-c7c2b2e37feb'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'VPN Gateway'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'IdleVNetGateway'; Message = 'Delete or reconfigure idle virtual network gateways'; State = 'Active'; Version = 1.1; Description = 'Repurpose or delete idle virtual network gateways'; LongDescription = 'We noticed that your virtual network gateway has been idle for over 90 days. This gateway is being billed hourly. You may want to reconfigure this gateway, or delete it if you don''t intend to use it anymore.'; Benefits = 'savings'; Tip = 'You can delete or repurpose idle virtual network gateways to reduce your Azure spend.'; LearnMoreLink = 'https://aka.ms/aa_idlevpngateway_learnmore'; }
        ,[PSCustomObject]@{ Id = 'f78c8e26-9c40-4a74-a091-f76aecb49099'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'VPN Gateway'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'HighP2SConnectionsVNetGateway'; Message = 'P2S Gateway with High Connection Count'; State = 'Active'; Version = 1; Description = 'Consider increasing the size of your VNet Gateway SKU to address high P2S use'; LongDescription = 'Each gateway SKU can only support a specified count of concurrent P2S connections. Your connection count is close to your gateway limit, so additional connection attempts may fail.'; Benefits = 'Increasing the size of your gateway will allow you to support more concurrent P2S users'; Tip = ''; LearnMoreLink = 'https://aka.ms/HighP2SConnectionsVNetGateway'; }
        ,[PSCustomObject]@{ Id = 'c249dc0e-9a17-423e-838a-d72719e8c5dd'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'VPN Gateway'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'VNetGatewayActiveActive'; Message = 'Enable Active-Active gateways for redundancy'; State = 'Active'; Version = 6; Description = 'Enable Active-Active gateways for redundancy'; LongDescription = 'In active-active configuration, both instances of the VPN gateway establish site-to-site (S2S) VPN tunnels to your on-premise VPN device. When a planned maintenance or unplanned event happens to one gateway instance, traffic is automatically switched over to the other active IPsec tunnel.'; Benefits = 'Ensure business continuity through connection resilience'; Tip = 'Enable Active-Active configuration for your VPN gateways for redundancy.'; LearnMoreLink = 'https://aka.ms/aa_vpnha_learnmore'; }
        ,[PSCustomObject]@{ Id = '2e41fe84-7173-4fe9-b257-61aa4679c3fe'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'VPN Gateway'; ResourceType = 'microsoft.network/virtualnetworkgateways'; Key = 'HighCPUVNetGateway'; Message = 'VPN Gateway with High CPU'; State = 'Active'; Version = 1; Description = 'Consider increasing the size of your VNet Gateway SKU to address consistently high CPU use'; LongDescription = 'Under high traffic load, the VPN gateway may drop packets due to high CPU. You should consider upgrading your VPN Gateway SKU since your VPN has consistently been running at .'; Benefits = 'Increasing the size of your VPN gateway will ensure that connections aren''t dropped due to high CPU'; Tip = ''; LearnMoreLink = 'https://aka.ms/HighCPUP2SVNetGateway'; }
        ,[PSCustomObject]@{ Id = '7c27d589-c7ed-47e1-8fe9-fe12ea81634a'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Network Watcher'; ResourceType = 'microsoft.network/networksecuritygroups'; Key = 'NSGFlowLogsenableTA'; Message = 'Enable Traffic Analytics for NSG'; State = 'Active'; Version = 2; Description = 'Enable Traffic Analytics to view insights into traffic patterns across Azure resources'; LongDescription = 'Traffic Analytics is a cloud-based solution that provides visibility into user and application activity in Azure. Traffic analytics analyzes Network Watcher network security group (NSG) flow logs to provide insights into traffic flow. With traffic analytics, you can view top talkers across Azure and non Azure deployments, investigate open ports, protocols and malicious flows in your environment and optimize your network deployment for performance. You can process flow logs at 10 mins and 60 mins processing intervals, giving you faster analytics on your traffic.'; Benefits = 'Identify top talkers, traffic hotspots, resource utilisation and security based on traffic patterns in NSG'; Tip = '* Use 10 mins processing interval for faster insights. * You can select Flog Logs version 2 to see bytes and packets per flow'; LearnMoreLink = 'https://aka.ms/aa_enableta_learnmore'; }
        ,[PSCustomObject]@{ Id = '6f087e7e-afdf-4a3d-a1de-41d70404b9cb'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'CloudNet_NetworkWatcher'; ResourceType = 'microsoft.network/networkwatchers/flowlogs'; Key = 'UpgradeNSGToVnetFlowLog'; Message = 'Upgrade NSG flow logs to VNet flow logs'; State = 'Active'; Version = 9; Description = 'Upgrade NSG flow logs to VNet flow logs'; LongDescription = 'Virtual Network flow log allows you to record IP traffic flowing in a virtual network. It provides several benefits over Network Security Group flow log like simplified enablement, enhanced coverage, accuracy, performance and observability of Virtual Network Manager rules and encryption status.'; Benefits = 'Improved coverage and additional functionality'; Tip = ''; LearnMoreLink = 'https://aka.ms/vnetflowlogspreviewdocs'; }
        ,[PSCustomObject]@{ Id = 'a15a8fbc-22ac-41af-9039-2c9c4a7128e0'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Azure Advisor'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'AddMonitorProdVM'; Message = ' Add Azure Monitor to your virtual machine (VM) labeled as production'; State = 'Disabled'; Version = 4.1; Description = 'Add Azure Monitor to your virtual machine (VM) labeled as production'; LongDescription = 'Azure Monitor for VMs monitors your Azure virtual machines (VM) and virtual machine scale sets at scale. It analyzes the performance and health of your Windows and Linux VMs,  and it monitors their processes and dependencies on other resources and external processes. It includes support for monitoring performance and application dependencies for VMs that are hosted on-premises or in another cloud provider.'; Benefits = ' Azure Monitor analyzes performance, health, and processes on your Windows and Linux VMs'; Tip = 'Azure Monitor analyzes performance, health, and processes on your Windows and Linux VMs'; LearnMoreLink = 'https://docs.microsoft.com/azure/azure-monitor/insights/vminsights-overview'; }
        ,[PSCustomObject]@{ Id = 'bbd39b95-99d9-4b7e-b66b-8813081307c1'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Advisor'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'AddTagPolicy'; Message = 'Assign the ''Add or replace a tag on resources'' policy'; State = 'Disabled'; Version = 2.1; Description = 'Enforce ''Add or replace a tag on resources'' using Azure Policy'; LongDescription = 'Azure Policy is a service in Azure that you use to create, assign, and manage policies. These policies enforce different rules and effects over your resources. This policy adds or replaces the specified tag and value when any resource is created or updated. Existing resources can be remediated by triggering a remediation task. Does not modify tags on resource groups.'; Benefits = 'This specific policy allows for resources to be tagged or for tags to be replaced.'; Tip = 'Azure Policy allows IT admins to define, assign, and, manage standards for resources in their environment.'; LearnMoreLink = 'https://docs.microsoft.com/azure/governance/policy/overview'; }
        ,[PSCustomObject]@{ Id = '6d395b56-af36-4f01-a814-f8ba08e49c24'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Advisor'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'AllowedLocationsPolicy'; Message = 'Assign the ''Allowed locations'' policy'; State = 'Disabled'; Version = 2.1; Description = 'Enforce ''Allowed locations'' using Azure Policy'; LongDescription = 'Azure Policy is a service in Azure that you use to create, assign, and manage policies. These policies enforce different rules and effects over your resources. This policy enables you to restrict the locations your organization can specify when deploying resources. Use to enforce your geo-compliance requirements.'; Benefits = 'This specific policy restricts the available locations where your organization can deploy new resources.'; Tip = 'Azure Policy allows IT admins to define, assign, and, manage standards for resources in their environment.'; LearnMoreLink = 'https://docs.microsoft.com/azure/governance/policy/overview'; }
        ,[PSCustomObject]@{ Id = '56089005-f16c-4b41-ba0d-2eb3549292d5'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Advisor'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'AllowedVirtualMachineSkuPolicy'; Message = 'Assign the ''Allowed virtual machine SKUs'' policy'; State = 'Disabled'; Version = 2.1; Description = 'Enforce ''Allowed virtual machine SKUs'' using Azure Policy'; LongDescription = 'Azure Policy is a service in Azure that you use to create, assign, and manage policies. These policies enforce different rules and effects over your resources. This policy enables you to specify a set of virtual machine SKUs that your organization can deploy.'; Benefits = 'This specific policy restricts the virtual machine SKUs that your organization can deploy.'; Tip = 'Azure Policy allows IT admins to define, assign, and, manage standards for resources in their environment.'; LearnMoreLink = 'https://docs.microsoft.com/azure/governance/policy/overview'; }
        ,[PSCustomObject]@{ Id = '142bcb82-aa8d-44e8-98d4-2b39125f2324'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Azure Advisor'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'AuditForManagedDisksPolicy'; Message = 'Enforce ''Audit VMs that do not use managed disks'' using Azure Policy'; State = 'Disabled'; Version = 2.1; Description = 'Enforce ''Audit VMs that do not use managed disks'' using Azure Policy'; LongDescription = 'Azure Policy is a service in Azure that you use to create, assign, and manage policies. These policies enforce different rules and effects over your resources. This policy audits VMs that do not use managed disks.'; Benefits = 'This specific policy audits VMs that do not use managed disks.'; Tip = 'Azure Policy allows IT admins to define, assign, and, manage standards for resources in their environment.'; LearnMoreLink = 'https://docs.microsoft.com/azure/governance/policy/overview'; }
        ,[PSCustomObject]@{ Id = 'b2c74ac7-061e-4164-b272-ecb0940f5965'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Advisor'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'InheritTagPolicy'; Message = 'Assign the ''Inherit a tag from the resource group'' policy'; State = 'Disabled'; Version = 2.1; Description = 'Enforce ''Inherit a tag from the resource group'' using Azure Policy'; LongDescription = 'Azure Policy is a service in Azure that you use to create, assign, and manage policies. These policies enforce different rules and effects over your resources. This policy adds or replaces the specified tag and value from the parent resource group when any resource is created or updated. Existing resources can be remediated by triggering a remediation task.'; Benefits = 'This specific policy allows for resources to inherit tags from the parent resource group.'; Tip = 'Azure Policy allows IT admins to define, assign, and, manage standards for resources in their environment.'; LearnMoreLink = 'https://docs.microsoft.com/azure/governance/policy/overview'; }
        ,[PSCustomObject]@{ Id = 'c6b94711-f1f5-4e7e-9c89-c17ed4190969'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'PremiumBlobStorageAccount'; Message = 'Use premium performance block blob storage'; State = 'Active'; Version = 1.1; Description = 'Use premium performance block blob storage'; LongDescription = 'One or more of your storage accounts has a high transaction rate per GB of block blob data stored. Use premium performance block blob storage instead of standard performance storage for your workloads that require fast storage response times and/or high transaction rates and potentially save on storage costs.'; Benefits = 'Block blob storage performance boost with the lowest Azure transaction prices.'; Tip = 'You can use Premium Blobs on high transaction blobs to boost performance.'; LearnMoreLink = 'https://aka.ms/usePremiumBlob'; }
        ,[PSCustomObject]@{ Id = '5b8ddf04-be28-44ec-ab2c-a63a34d1de13'; Category = 'Cost'; Control = ''; Impact = 'High'; ServiceName = 'ReservedInstances'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'AppServiceReservedCapacity'; Message = 'Buy reserved instance'; State = 'Active'; Version = 12.1; Description = 'Consider App Service reserved instance to save over your on-demand costs'; LongDescription = 'We analyzed your App Service usage pattern over selected Term, look-back period, and recommend reserved instance purchase that maximizes your savings. With reserved instance, you can prepurchase hourly usage for the App Service plan and save over your Pay-as-you-go costs. Reserved instance is a billing benefit and is automatically applied to new or existing deployments. Saving estimates are calculated for individual subscriptions based on usage pattern over selected Term, look-back period.'; Benefits = 'savings'; Tip = 'You can buy Azure AppService reserved instance to save money over pay-as-you-go costs.'; LearnMoreLink = 'https://aka.ms/rirecommendations'; }
