        ,[PSCustomObject]@{ Id = '89515250-1243-43d1-b4e7-f9437cedffd8'; Category = 'Cost'; Control = ''; Impact = 'High'; ServiceName = 'ReservedInstances'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'SavingsPlan'; Message = 'Add a plan'; State = 'Active'; Version = 4; Description = 'Purchasing a savings plan for compute could unlock lower prices'; LongDescription = 'We analyzed your compute usage over the last 30 days and recommend adding a savings plan to increase your savings. The savings plan unlocks lower prices on select compute services when you commit to spend a fixed hourly amount for 1 or 3 years. As you use select compute services globally, your usage is covered by the plan at reduced prices. During the times when your usage is above your hourly commitment, you''ll simply be billed at your regular pay-as-you-go prices. With savings automatically applying across compute usage globally, you''ll continue saving even as your usage needs change over time. Savings plan are more suited for dynamic workloads while accommodating for planned or unplanned changes while reservations are more suited for stable, predictable workloads with no planned changes. Saving estimates are calculated for individual subscriptions and the usage pattern observed over last 30 days. Shared scope savings plans are available in purchase experience and can further increase savings.'; Benefits = 'savings'; Tip = 'You can add a savings plan and save money over on-demand costs.'; LearnMoreLink = 'https://aka.ms/savingsplan-compute'; }
