        ,[PSCustomObject]@{ Id = 'ef14bcc2-41a5-41f6-bca8-10764cfbdee0'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'CreateTableStatisticsSqlDW'; Message = 'Create table statistics'; State = 'Active'; Version = 3.1; Description = 'Create statistics on table columns'; LongDescription = 'We have detected that you are missing table statistics which may be impacting query performance. The query optimizer uses statistics to estimate the cardinality or number of rows in the query result which enables the query optimizer to create a high quality query plan.'; Benefits = 'Increase query performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/learnmorestatistics'; }
        ,[PSCustomObject]@{ Id = '9d7196d1-2d7c-4316-820f-7374a4ddf250'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'DataSkewSqlDW'; Message = 'Remove data skew'; State = 'Active'; Version = 3.1; Description = 'Remove data skew to increase query performance'; LongDescription = 'We have detected distribution data skew greater than 15%. This can cause costly performance bottlenecks.'; Benefits = 'Increase query performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/learnmoredataskew'; }
        ,[PSCustomObject]@{ Id = 'dd93fbbf-e5ef-4c7c-886e-2bfef0958f45'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'FileSplittingGuidance'; Message = 'Split files that are staged for loading'; State = 'Active'; Version = 1; Description = 'Split staged files in the storage account to increase load performance'; LongDescription = 'We have detected that you can increase load throughput by splitting your compressed files that are staged in your storage account. A good rule of thumb is to split compressed files into 60 or more to maximize the parallelism of your load. '; Benefits = 'Increase load performance'; Tip = 'You can improve load performance by splitting files that are staged for loading.'; LearnMoreLink = 'https://aka.ms/learnmorefilesplit'; }
        ,[PSCustomObject]@{ Id = 'e0ba1234-61da-46e3-a66d-fa0752b9df7d'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'LoadBatchSizeGuidance'; Message = 'Increase batch size when loading '; State = 'Active'; Version = 1; Description = 'Increase batch size when loading to maximize load throughput, data compression, and query performance'; LongDescription = 'We have detected that you can increase load performance and throughput by increasing the batch size when loading into your database. You should consider using the COPY statement. If you are unable to use the COPY statement, consider increasing the batch size when using loading utilities such as the SQLBulkCopy API or BCP - a good rule of thumb is a batch size between 100K to 1M rows.'; Benefits = 'Increase load throughput, data compression, and query performance'; Tip = 'You can improve load throughput, data compression, and query performance by increasing your batch size.'; LearnMoreLink = 'https://aka.ms/learnmoreincreasebatchsize'; }
        ,[PSCustomObject]@{ Id = '14b28bdb-b83d-4f55-a516-44d4152f1f2b'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'SqlDwIncreaseCacheCapacity'; Message = 'Increase Cache Capacity'; State = 'Active'; Version = 1; Description = 'Scale up to optimize cache utilization with SQL Data Warehouse'; LongDescription = 'We have detected that you had high cache used percentage with a low hit percentage. This indicates high cache eviction which can impact the performance of your workload.'; Benefits = 'Increase query performance'; Tip = 'You can improve your SQL Data Warehouse query performance by scaling up to optimize cache utilization.'; LearnMoreLink = 'https://aka.ms/learnmoreadaptivecache'; }
        ,[PSCustomObject]@{ Id = '33e515fe-354c-4016-a0f7-c4d6585aea61'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'SqlDwReduceTempdbContention'; Message = 'Reduce Tempdb Contention'; State = 'Active'; Version = 1; Description = 'Scale up or update resource class to reduce tempdb contention with SQL Data Warehouse'; LongDescription = 'We have detected that you had high tempdb utilization which can impact the performance of your workload.'; Benefits = 'Increase query performance'; Tip = 'You can improve your SQL Data Warehouse query performance by scaling up or updating your resource class to reduce tempdb contention.'; LearnMoreLink = 'https://aka.ms/learnmoretempdb'; }
        ,[PSCustomObject]@{ Id = '293984cf-b551-461f-b22d-9659ebd09a4f'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'SqlDwReplicateTable'; Message = 'Replicate Table'; State = 'Active'; Version = 1; Description = 'Convert tables to replicated tables with SQL Data Warehouse'; LongDescription = 'We have detected that you may benefit from using replicated tables. When using replicated tables, this will avoid costly data movement operations and significantly increase the performance of your workload.'; Benefits = 'Increase query performance'; Tip = 'You can improve your SQL Data Warehouse query performance by converting tables to replicated tables.'; LearnMoreLink = 'https://aka.ms/learnmorereplicatedtables'; }
        ,[PSCustomObject]@{ Id = '314a2614-24d3-496c-b9d6-e6cd3df4b6c2'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'ColocateStorageAccount'; Message = 'Co-locate the storage account and the SQL pool within the same region'; State = 'Active'; Version = 1; Description = 'Co-locate the storage account within the same region to minimize latency when loading'; LongDescription = 'We have detected that you are loading from a region that is different from your SQL pool. You should consider loading from a storage account that is within the same region as your SQL pool to minimize latency when loading data.'; Benefits = 'Minimize latency and increase load performance'; Tip = 'You can minimize latency and increase load performance by co-locating your storage account with your SQL pool'; LearnMoreLink = 'https://aka.ms/learnmorestoragecolocation'; }
        ,[PSCustomObject]@{ Id = '01dea77b-3ca4-4583-9b09-88f5a8fd5857'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.sql/sqldatawarehouses'; Key = 'UpdateTableStatisticsSqlDW'; Message = 'Update table statistics'; State = 'Active'; Version = 3.1; Description = 'Update statistics on table columns'; LongDescription = 'We have detected that you do not have up-to-date table statistics which may be impacting query performance. The query optimizer uses up-to-date statistics to estimate the cardinality or number of rows in the query result which enables the query optimizer to create a high quality query plan.'; Benefits = 'Increase query performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/learnmorestatistics'; }
        ,[PSCustomObject]@{ Id = '48eda464-1485-4dcf-a674-d0905df5054a'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/disks'; Key = 'DeleteOrDowngradeUnattachedDisks'; Message = 'You have disks that are not attached to a VM. Please evaluate if you still need the disk.'; State = 'Active'; Version = 18.1; Description = 'You have disks that are not attached to a VM. Please evaluate if you still need the disk'; LongDescription = 'We have observed that you have disks that are not attached to a VM. Please evaluate if you still need the disk. If you decide to delete the disk, recovery is not possible. We recommend that you create a snapshot before deletion or ensure the data in the disk is no longer required.'; Benefits = 'Cost saving.'; Tip = 'Delete unattached disks.'; LearnMoreLink = 'https://aka.ms/unattacheddisks'; }
        ,[PSCustomObject]@{ Id = 'dd65e838-4473-4fdb-b124-e09798e35f36'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'EnableLargeFileShares'; Message = 'Enable the large file share feature on storage account to avoid throttling'; State = 'Disabled'; Version = 1.1; Description = 'Enable the large file share feature on storage account to avoid throttling.'; LongDescription = 'Standard file shares are limited to 1000 IOPS per share. By enabling the large file share feature you can get 10x more IOPS. There is no charge to enable large file shares. Enabling large files shares is an irreversible action. Large file shares enabled account can''t be converted to re-redundant accounts.'; Benefits = 'Eliminate throttling and improve the performance of your file shares.'; Tip = 'By enabling the large file share feature you can get 10x more IOPS. There is no charge to enable large file shares.'; LearnMoreLink = 'https://docs.microsoft.com/azure/storage/files/storage-files-how-to-create-large-file-share#enable-large-files-shares-on-an-existing-account'; }
        ,[PSCustomObject]@{ Id = 'd9823f54-3eaa-485b-a3b0-b9559c8e831f'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'EnableSnapshots'; Message = 'No Snapshots Detected'; State = 'Active'; Version = 1.1; Description = 'No Snapshots Detected'; LongDescription = 'We have observed that there are no snapshots of your file shares. This means you are not protected from accidental file deletion or file corruption scenarios. Please enable snapshots to protect your data.  To enable snapshots you can use Azure Portal or Azure Backup or 3rd party solutions'; Benefits = 'Schedule snapshots of your file shares to protect yourself from accidental file deletion or data corruption like ransomware.'; Tip = ''; LearnMoreLink = 'https://learn.microsoft.com/en-us/azure/storage/files/storage-snapshots-files'; }
        ,[PSCustomObject]@{ Id = '386452d3-8df0-4174-94cb-fee063b3084f'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'XstoreLargeClassicLog'; Message = 'Reduce retention policy of classic storage logs'; State = 'Active'; Version = 1; Description = 'Revisit retention policy for classic log data in storage accounts'; LongDescription = 'Large classic log data is detected on your storage accounts. You are billed on capacity of data stored in storage accounts including classic logs. You are recommended to check the retention policy of classic logs and update with necessary period to retain less log data. This would reduce unnecessary classic log data and save your billing cost from less capacity.'; Benefits = 'Save cost from unneeded log data'; Tip = ''; LearnMoreLink = ' https://docs.microsoft.com/azure/storage/common/manage-storage-analytics-logs#modify-retention-policy'; }
        ,[PSCustomObject]@{ Id = 'ad488f61-5ada-4c72-8296-56ea29955552'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'MigrateToPremiumStorage'; Message = 'Migrate from Standard Storage to Premium Storage'; State = 'Disabled'; Version = 1.1; Description = 'Migrate your data from Standard Storage account to Premium Storage account'; LongDescription = 'We noticed that there is a high volume of transactions on your storage account. We recommend you to migrate to Premium Storage (SSD based) to boost your performance on Page Blobs/Unmanaged Disks.'; Benefits = 'Improve your performance with lower latency and provisioned performance'; Tip = ''; LearnMoreLink = 'https://aka.ms/aa_premiumstg_migrate_learnmore'; }
        ,[PSCustomObject]@{ Id = '6747b02b-b6ac-4c2e-aeca-c2aa0438f58d'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'MixedDiskTypeToSSDPublic'; Message = 'Use SSD Disks for your production workloads'; State = 'Active'; Version = 1.1; Description = 'Use SSD Disks for your production workloads'; LongDescription = 'We noticed that you are using SSD disks while also using Standard HDD disks on the same VM. Standard HDD managed disks are generally recommended for dev-test and backup; we recommend you use Premium SSDs or Standard SSDs for production. Premium SSDs deliver high-performance and low-latency disk support for virtual machines with IO-intensive workloads. Standard SSDs provide consistent and lower latency. Upgrade your disk configuration today for improved latency, reliability, and availability. Upgrading requires a VM reboot, which will take three to five minutes.'; Benefits = 'Improve latency, reliability, and availability'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/windows/disks-types#disk-comparison'; }
        ,[PSCustomObject]@{ Id = 'c81a0349-18c0-4bd2-81c7-475adde922d2'; Category = 'Cost'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'MoveToPremiumStorage'; Message = 'Consider evaluating premium storage from a cost perspective'; State = 'Active'; Version = 1.1; Description = 'Based on your high transactions/TB  ratio, there''s a possibility that premium storage might be more cost effective in addition to being performant for your scenario. More details on pricing for premium and standard accounts can be found here'; LongDescription = 'The customer can lower the bill if the transactions/TB ratio is high. Exact number would depend on transaction mix and region but anywhere >30 or 35 TPB/TB may be good candidates to at least evaluate a move to premium storage.'; Benefits = 'Based on your high transactions/TB  ratio, there is a possibility that premium storage might be more cost effective in addition to being performant for your scenario.'; Tip = ''; LearnMoreLink = 'https://aka.ms/azureblobstoragepricing'; }
        ,[PSCustomObject]@{ Id = 'a0ad4f8c-f904-4b11-955d-e0044473c5fa'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StorageAccountScaleTarget'; Message = 'Prevent hitting subscription limit for maximum storage accounts'; State = 'Active'; Version = 2; Description = 'Prevent hitting subscription limit for maximum storage accounts'; LongDescription = 'A region can support a maximum of 250 storage accounts per subscription. You have either already reached or are about to reach that limit. If you reach that limit, you will be unable to create any more storage accounts in that subscription/region combination. Please evaluate the recommended action below to avoid hitting the limit.'; Benefits = 'Ensure you do not reach the limit that can prevent you from creating additional storage accounts'; Tip = ''; LearnMoreLink = 'https://aka.ms/subscalelimit'; }
        ,[PSCustomObject]@{ Id = 'b353f187-4cb4-4b2b-b502-472f45f32fd6'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StorageCallPutBlob'; Message = 'Optimize Block Blob Operations'; State = 'Active'; Version = 1; Description = 'Use "Put Blob" for blobs smaller than 256 MB'; LongDescription = 'When writing a block blob that is 256 MB or less (64 MB for requests using REST versions before 2016-05-31), you can upload it in its entirety with a single write operation using "Put Blob". Based on your aggregated metrics, we believe your storage account''s write operations can be optimized.'; Benefits = 'Increase performance and reduce operation costs.'; Tip = ''; LearnMoreLink = 'http://aka.ms/understandblockblobs'; }
        ,[PSCustomObject]@{ Id = '1ef6e4c5-2caa-4528-a6e4-0db08a4d2872'; Category = 'Cost'; Control = ''; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StorageGPv1ToGPv2'; Message = 'Upgrade Storage Account Kind to GPv2'; State = 'Disabled'; Version = 1; Description = 'Upgrade your Storage Account to General-purpose v2'; LongDescription = 'General-purpose v2 (GPv2) accounts enables all the latest features at the lowest per gigabyte prices while retaining support for all APIs and features supported in General-purpose v1 (GPv1) and Blob storage accounts.'; Benefits = 'Take advantage of Storage''s latest features and pricing model'; Tip = ''; LearnMoreLink = 'https://aka.ms/storageaccounts'; }
        ,[PSCustomObject]@{ Id = '00c14add-2aef-4bb4-a3bd-5759096d4417'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.compute/disks'; Key = 'MDHDDtoPremiumForPerformance'; Message = 'Convert Standard HDD to Premium SSD for better performance'; State = 'Active'; Version = 1.2; Description = 'Convert Managed Disks from Standard HDD to Premium SSD for performance'; LongDescription = 'We have noticed your Standard HDD disk is approaching performance targets. Azure premium SSDs deliver high-performance and low-latency disk support for virtual machines with IO-intensive workloads. Give your disk performance a boost by upgrading your Standard HDD disk to Premium SSD disk. Upgrading requires a VM reboot, which will take three to five minutes.'; Benefits = 'Give your disk performance a boost using Premium SSD disks.'; Tip = 'Upgrade your Standard HDD disks to Premium SSD disks by following our instructions for the Azure portal, PowerShell, or CLI.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/windows/disks-types#premium-ssd'; }
        ,[PSCustomObject]@{ Id = 'd42d751d-682d-48f0-bc24-bb15b61ac4b8'; Category = 'High Availability'; Control = 'Scalability'; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StoragePremiumBlobQuotaLimit'; Message = 'Migrate Premium Disks from Unmanaged to Managed'; State = 'Active'; Version = 6; Description = 'Use Managed Disks for storage accounts reaching capacity limit'; LongDescription = 'When Premium SSD unmanaged disks in storage accounts are about to reach their Premium Storage capacity limit, failures might occur. To avoid failures when this limit is reached, migrate to Managed Disks that don''t have an account capacity limit. This migration can be done through the portal in less than 5 minutes.'; Benefits = 'Avoid scale issues when account reaches capacity limit'; Tip = ''; LearnMoreLink = 'https://aka.ms/premium_blob_quota'; }
        ,[PSCustomObject]@{ Id = '42dbf883-9e4b-4f84-9da4-232b87c4b5e9'; Category = 'High Availability'; Control = 'Business Continuity'; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StorageSoftDelete'; Message = 'Enable Soft Delete'; State = 'Active'; Version = 6; Description = 'Enable Soft Delete to protect your blob data'; LongDescription = 'Soft Delete puts deleted data into a "soft" deleted state instead of permanently deleted. When data is overwritten, a soft deleted snapshot is generated to save the state of the overwritten data. You can configure the amount of time soft deleted data is recoverable before it permanently expires.'; Benefits = 'Save and recover your data when blobs or blob snapshots are accidentally overwritten or deleted'; Tip = ''; LearnMoreLink = 'https://aka.ms/softdelete'; }
        ,[PSCustomObject]@{ Id = '8433e84a-8f5c-4b6c-9052-9b98757348ea'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StandardSSDForNonPremVM'; Message = 'Upgrade to Standard SSD Disks for consistent and improved performance'; State = 'Active'; Version = 2.1; Description = 'Upgrade to Standard SSD Disks for consistent and improved performance'; LongDescription = 'Because you are running IaaS virtual machine workloads on Standard HDD managed disks, we wanted to let you know that a Standard SSD disk option is now available for all Azure VM types. Standard SSD disks are a cost-effective storage option optimized for enterprise workloads that need consistent performance. Upgrade your disk configuration today for improved latency, reliability, and availability. Upgrading requires a VM reboot, which will take three to five minutes.'; Benefits = 'Improve disk latency, reliability, and availability using Standard SSD disks.'; Tip = 'Upgrade to Standard SSD Disks for consistent and improved performance by following our instructions for the Azure portal, PowerShell, or CLI.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/windows/disks-types#standard-ssd'; }
        ,[PSCustomObject]@{ Id = '70065997-d4d4-4671-baeb-39a2ac66e8ec'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'StorageUseLargeBlockSizesForPutBlock'; Message = 'Optimize Block Blob Operations'; State = 'Disabled'; Version = 1; Description = 'Use larger block sizes for large block blob uploads'; LongDescription = 'When writing a block blob that is larger than 256 MB, using larger block sizes reduces the number of write operations required to upload the blob when calling "Put Block". Block blobs support up to 100 MB blocks for the REST API versions 2016-05-31 and later, and 4 MB for older versions. We suggest using at least 4 MB blocks when uploading large objects with the "Put Block" operation. Based on your aggregated metrics, we believe your storage account''s write operations can be optimized.'; Benefits = 'Increase performance and reduce operation costs'; Tip = ''; LearnMoreLink = 'https://aka.ms/understandblockblobs'; }
        ,[PSCustomObject]@{ Id = '33557a7c-6dd6-4b46-9579-fc5273f07458'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'UMDHDDtoPremiumForPerformance'; Message = 'Convert Unmanaged Disks from Standard HDD to Premium SSD for performance'; State = 'Active'; Version = 1.1; Description = 'Convert Unmanaged Disks from Standard HDD to Premium SSD for performance'; LongDescription = 'We have noticed your Unmanaged HDD Disk is approaching performance targets. Azure premium SSDs deliver high-performance and low-latency disk support for virtual machines with IO-intensive workloads. Give your disk performance a boost by upgrading your Standard HDD disk to Premium SSD disk. Upgrading requires a VM reboot, which will take three to five minutes.'; Benefits = 'Give your disk performance a boost using Premium SSD disks.'; Tip = 'Upgrade your Unmanaged HDD disks to Premium SSD disks by following our instructions for the Azure portal, PowerShell, or CLI.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-machines/windows/disks-types#premium-ssd'; }
        ,[PSCustomObject]@{ Id = '3c374434-42e7-44db-8b0b-5b8ed970114b'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Storage'; ResourceType = 'microsoft.storage/storageaccounts'; Key = 'UpdateStorageJavaSDK'; Message = 'Update to newer releases of Azure Storage Java v12 SDK'; State = 'Active'; Version = 5; Description = 'Update to newer releases of the Storage Java v12 SDK for better reliability.'; LongDescription = 'We noticed that one or more of your applications use an older version of the Azure Storage Java v12 SDK to write data to Azure Storage. Unfortunately, the version of the SDK being used has a critical issue that uploads incorrect data during retries (for example, in case of HTTP 500 errors), resulting in an invalid object being written. The issue is fixed in newer releases of the Java v12 SDK.'; Benefits = 'The issue is fixed in newer releases of the Java v12 SDK.'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/developer/java/sdk/?view=azure-java-stable'; }
        ,[PSCustomObject]@{ Id = '3f000a35-4cf7-44ef-8127-2f7ed7909125'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'ExpressRoute'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'ExpressRouteCircuitO365'; Message = 'Create additional ExpressRoute circuits'; State = 'Disabled'; Version = 5; Description = 'Create additional ExpressRoute Circuits in another location for redundancy'; LongDescription = 'Customers using Microsoft Peering for Office 365 services should have at least two ExpressRoute Circuits in different locations to avoid a single point of failure. Create an additional ExpressRoute circuit to have a redundant connection in case of any failure.'; Benefits = 'Avoid a single point of failure'; Tip = 'You can avoid a single point of failure by creating an additional ExpressRoute circuit at a different location.'; LearnMoreLink = 'https://aka.ms/o365ERAdvisor'; }
        ,[PSCustomObject]@{ Id = 'c46ab673-2ddf-4a2c-927f-022616418bb0'; Category = 'Operational Excellence'; Control = ''; Impact = 'Low'; ServiceName = 'Microsoft Support'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'ResourceHealthAlert'; Message = 'Create an Azure Resource Health alert'; State = 'Disabled'; Version = 4; Description = 'Create an Azure Resource Health alert'; LongDescription = 'Resource Health alerts keeps you informed about the current and historical health status of your Azure resources. Azure Resource Health alerts can notify you in near real-time when these resources have a change in their health status.'; Benefits = 'Get notified of the health of your Azure resources'; Tip = 'You can create a Resource Health alert to get notified of the health status of your Azure resources.'; LearnMoreLink = 'https://docs.microsoft.com/azure/service-health/resource-health-alert-arm-template-guide'; }
        ,[PSCustomObject]@{ Id = 'c6ac1f03-bd58-4421-9522-23cffb64d8e1'; Category = 'Operational Excellence'; Control = ''; Impact = 'Low'; ServiceName = 'Microsoft Support'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'ServiceHealthAlert'; Message = 'Create an Azure service health alert'; State = 'Disabled'; Version = 3.1; Description = 'Create an Azure service health alert'; LongDescription = 'Service health alerts help you stay notified when Azure service issues affect you. Create a service health alert for the regions and services that you care about.'; Benefits = 'Get notified when Azure service issues affect you'; Tip = 'You can create a service health alert to get notified when an Azure service issue affects you.'; LearnMoreLink = 'https://aka.ms/aa_servicehealthalert_action'; }
        ,[PSCustomObject]@{ Id = '8189a205-7f30-4f97-90ab-230519248722'; Category = 'Operational Excellence'; Control = ''; Impact = 'High'; ServiceName = 'Microsoft Support'; ResourceType = 'microsoft.subscriptions/subscriptions'; Key = 'SupportPlan'; Message = 'Upgrade your support plan'; State = 'Disabled'; Version = 3.1; Description = 'Upgrade to a support plan that includes technical support'; LongDescription = 'We recommend upgrading your support plan to include technical support. Explore the range of Azure support options and choose the plan that best fits, whether you''re a developer just starting your cloud journey or a large organization deploying business-critical, strategic applications.'; Benefits = 'Ensure business continuity by having access to Azure cloud experts'; Tip = 'You can upgrade your support plan to include technical support.'; LearnMoreLink = 'https://aka.ms/azuresupport'; }
        ,[PSCustomObject]@{ Id = 'afdf4c1a-e46b-4817-a5d6-4b9909f58e2a'; Category = 'Cost'; Control = ''; Impact = 'Low'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.synapse/workspaces'; Key = 'EnableSynapseSparkComputeAutoPauseGuidance'; Message = 'Consider enabling automatic pause feature on spark compute'; State = 'Active'; Version = 3.1; Description = 'Consider enabling automatic pause feature on spark compute'; LongDescription = 'Automatic pause releases and shuts down unused compute resources after a set idle period of inactivity.'; Benefits = 'Automatic pause releases and shuts down unused compute resources after a set idle period of inactivity'; Tip = 'Automatic pause releases and shuts down unused compute resources after a set idle period of inactivity.'; LearnMoreLink = 'https://aka.ms/EnableSynapseSparkComputeAutoPauseGuidance'; }
        ,[PSCustomObject]@{ Id = '00b1ef72-4d0f-4452-a6a8-1df5397172d6'; Category = 'Cost'; Control = ''; Impact = 'Low'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.synapse/workspaces'; Key = 'EnableSynapseSparkComputeAutoScaleGuidance'; Message = 'Consider enabling autoscale feature on spark compute'; State = 'Active'; Version = 3.1; Description = 'Consider enabling autoscale feature on spark compute'; LongDescription = 'Apache Spark for Azure Synapse Analytics pool''s Autoscale feature automatically scales the number of nodes in a cluster instance up and down. During the creation of a new Apache Spark for Azure Synapse Analytics pool, a minimum and maximum number of nodes can be set when Autoscale is selected. Autoscale then monitors the resource requirements of the load and scales the number of nodes up or down. There''s no extra charge for this feature.'; Benefits = 'The Autoscale feature monitors resource requirements and automatically scales the number of nodes up and down to meet the compute and memory requirements, thereby optimizing costs from unused resources.'; Tip = 'Enabling autoscale comes at 0 cost and will reduce job failures.'; LearnMoreLink = 'https://aka.ms/EnableSynapseSparkComputeAutoScaleGuidance'; }
        ,[PSCustomObject]@{ Id = 'a14a77e7-1187-4714-9042-7c6056b30017'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.synapse/workspaces'; Key = 'SynapseLoadBatchSizeGuidance'; Message = 'Increase batch size when loading '; State = 'Disabled'; Version = 6; Description = 'Increase batch size when loading to maximize load throughput, data compression, and query performance'; LongDescription = 'We have detected that you can increase load performance and throughput by increasing the batch size when loading into your database. You should consider using the COPY statement. If you are unable to use the COPY statement, consider increasing the batch size when using loading utilities such as the SQLBulkCopy API or BCP - a good rule of thumb is a batch size between 100K to 1M rows.'; Benefits = 'Increase load throughput, data compression, and query performance'; Tip = 'You can improve load throughput, data compression, and query performance by increasing your batch size.'; LearnMoreLink = 'https://aka.ms/learnmoreincreasebatchsize'; }
        ,[PSCustomObject]@{ Id = '7c83695a-3fa9-4668-9080-85151f5ab7be'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Synapse Analytics'; ResourceType = 'microsoft.synapse/workspaces'; Key = 'UpgradeSynapseManagementClientSDK'; Message = 'Update SynapseManagementClient'; State = 'Active'; Version = 6; Description = 'Update SynapseManagementClient SDK Version'; LongDescription = 'New SynapseManagementClient is using .NET SDK 4.0 or above.'; Benefits = 'Latest SynapseManagementClient Libraries contain fixes for known issues and additional improvements.'; Tip = ''; LearnMoreLink = 'https://aka.ms/UpgradeSynapseManagementClientSDK'; }
        ,[PSCustomObject]@{ Id = 'c8bbcb72-b778-48b4-882c-d8ce271995e5'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Text Analytics'; ResourceType = 'microsoft.cognitiveservices/accounts'; Key = 'UpgradeToLatestAPI'; Message = 'Upgrade to the latest Cognitive Service Text Analytics API version'; State = 'Active'; Version = 3; Description = 'Upgrade to the latest Cognitive Service Text Analytics API version'; LongDescription = 'Please upgrade to the latest API version to get the best results in terms of model quality, performance and service availability. Also there are new features are available as new endpoints starting from V3.0 such as PII recognition, Entity recognition and entity linking available as separate endpoints. In terms of changes in preview endpoints we have Opinion Mining in SA endpoint, redacted text property in PII endpoint'; Benefits = 'Better service availability and performance'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cognitive-services/text-analytics/how-tos/text-analytics-how-to-call-api'; }
        ,[PSCustomObject]@{ Id = '1b94aa41-a51e-4cad-98fb-3a44447d5997'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Text Analytics'; ResourceType = 'microsoft.cognitiveservices/accounts'; Key = 'UpgradeToLatestSDK'; Message = 'Upgrade to the latest SDK version for Text Analytics'; State = 'Active'; Version = 3; Description = 'Upgrade to the latest Cognitive Service Text Analytics SDK version'; LongDescription = 'Please upgrade to the latest SDK version to get the best results in terms of model quality, performance and service availability. Also there are new features are available as new endpoints starting from V3.0 such as PII recognition, Entity recognition and entity linking available as separate endpoints. In terms of changes in preview endpoints we have Opinion Mining in SA endpoint, redacted text property in PII endpoint'; Benefits = 'Better service availability and performance'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/cognitive-services/text-analytics/quickstarts/text-analytics-sdk?tabs=version-3-1&pivots=programming-language-csharp'; }
        ,[PSCustomObject]@{ Id = '843606b2-e019-495b-bf81-3cb4107aab6a'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/kubeenvironments'; Key = 'AppServiceContainerAppsResourceProviderMigration'; Message = 'Azure Container Apps: Namespace migration from Microsoft.Web to Microsoft.App'; State = 'Disabled'; Version = 6; Description = 'Azure Container Apps: Namespace migration from Microsoft.Web to Microsoft.App'; LongDescription = 'During March 2022, Azure Container Apps resources will be gradually migrated from the Microsoft.Web namespace to Microsoft.App. This will affect some management operations. Availability of your Container Apps resources will not be impacted.'; Benefits = 'Keep your apps healthy and highly available'; Tip = ''; LearnMoreLink = 'https://aka.ms/microsoftappmigration'; }
        ,[PSCustomObject]@{ Id = '1294987d-c97d-41d0-8fd8-cb6eab52d87b'; Category = 'High Availability'; Control = 'Scalability'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceCPUExhaustion'; Message = 'High CPU'; State = 'Active'; Version = 6; Description = 'Scale out your App Service plan to avoid CPU exhaustion'; LongDescription = 'High CPU utilization can lead to runtime issues with applications. Your application exceeded 90% CPU over the last couple of days. To reduce CPU usage and avoid runtime issues, scale out the application.'; Benefits = 'Keep your app healthy'; Tip = ''; LearnMoreLink = 'https://aka.ms/antbc-cpu'; }
        ,[PSCustomObject]@{ Id = 'a85f5f1c-c01f-4926-84ec-700b7624af8c'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceServiceHealthIssues'; Message = 'Service Health Issue'; State = 'Active'; Version = 5; Description = 'Check your app''s service health issues'; LongDescription = 'We have a recommendation related to your app''s service health. Please open the Azure Portal, go to the app, click the Diagnose and Solve to see more details.'; Benefits = 'Keep your app healthy'; Tip = ''; LearnMoreLink = 'https://docs.microsoft.com/azure/app-service/app-service-best-practices'; }
        ,[PSCustomObject]@{ Id = 'b30897cc-2c2e-4677-a2a1-107ae982ff49'; Category = 'High Availability'; Control = 'Disaster Recovery'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceFixBackupDatabaseSettings'; Message = 'Fix backup database settings'; State = 'Active'; Version = 7; Description = 'Fix the backup database settings of your App Service resource'; LongDescription = 'When an application has an invalid database configuration, its backups fail. For details, see your application''s backup history on your app management page.'; Benefits = 'Ensure business continuity'; Tip = ''; LearnMoreLink = 'https://aka.ms/antbc'; }
        ,[PSCustomObject]@{ Id = '80efd6cb-dcee-491b-83a4-7956e9e058d5'; Category = 'High Availability'; Control = 'Disaster Recovery'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceFixBackupStorageSettings'; Message = 'Fix backup storage settings'; State = 'Active'; Version = 7; Description = 'Fix the backup storage settings of your App Service resource'; LongDescription = 'When an application has invalid storage settings, its backups fail. For details, see your application''s backup history on your app management page.'; Benefits = 'Ensure business continuity'; Tip = ''; LearnMoreLink = 'https://aka.ms/antbc'; }
        ,[PSCustomObject]@{ Id = '66d3137a-c4da-4c8a-b6b8-e03f5dfba66e'; Category = 'High Availability'; Control = 'Scalability'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceMemoryExhaustion'; Message = 'High memory'; State = 'Active'; Version = 6; Description = 'Scale up your App Service plan SKU to avoid memory problems'; LongDescription = 'The App Service Plan containing your application exceeded 85% memory allocation. High memory consumption can lead to runtime issues your applications. Find the problem application and  scale it up to a higher plan with more memory resources.'; Benefits = 'Keep your app healthy'; Tip = ''; LearnMoreLink = 'https://aka.ms/antbc-memory'; }
        ,[PSCustomObject]@{ Id = '9ebff5d5-10c1-4fed-8c58-1954e27d3bfa'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceMoveToPremiumV2'; Message = 'Try PremiumV2 SKU'; State = 'Active'; Version = 1.1; Description = 'Move your App Service Plan to PremiumV2 for better performance'; LongDescription = 'Your app served more than 1000 requests per day for the past 3 days. Your app may benefit from the higher performance infrastructure available with the Premium V2 App Service tier. The Premium V2 tier features Dv2-series VMs with faster processors, SSD storage, and doubled memory-to-core ratio when compared to the previous instances. Learn more about upgrading to Premium V2 from our documentation.'; Benefits = 'Obtain better performance with lower cost'; Tip = ''; LearnMoreLink = 'https://aka.ms/ant-premiumv2'; }
        ,[PSCustomObject]@{ Id = '45cfc38d-3ffd-4088-bb15-e4d0e1e160fe'; Category = 'High Availability'; Control = 'Scalability'; Impact = 'Medium'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/serverfarms'; Key = 'AppServiceNumberOfInstances'; Message = 'Consider scaling out your App Service Plan to optimize user experience and availability'; State = 'Active'; Version = 5; Description = 'Scale out your App Service plan'; LongDescription = 'Consider scaling out your App Service Plan to at least two instances to avoid cold start delays and service interruptions during routine maintenance.'; Benefits = 'Optimize user experience and availability'; Tip = ''; LearnMoreLink = 'https://aka.ms/appsvcnuminstances'; }
        ,[PSCustomObject]@{ Id = '07f9a07d-9030-465c-89dc-b1f712334b83'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceOutboundConnections'; Message = 'Many outbound connections'; State = 'Active'; Version = 1.1; Description = 'Check outbound connections from your App Service resource'; LongDescription = 'Your app has opened too many TCP/IP socket connections. Exceeding ephemeral TCP/IP port connection limits can cause unexpected connectivity issues for your apps.'; Benefits = 'Better performance and lower cost'; Tip = ''; LearnMoreLink = 'https://aka.ms/antbc-socket'; }
        ,[PSCustomObject]@{ Id = '3e35f804-52cb-4ebf-84d5-d15b3ab85dfc'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceProactiveCrashMonitoring'; Message = 'Worker process crashed due to Unhandled Exception'; State = 'Active'; Version = 7; Description = 'Fix application code, a worker process crashed due to an unhandled exception'; LongDescription = 'A worker process in your application crashed due to an unhandled exception. To identify the root cause, collect memory dumps and call stack information at the time of the crash.'; Benefits = 'Keep your app healthy and highly available'; Tip = ''; LearnMoreLink = 'https://aka.ms/appsvcproactivecrashmonitoring'; }
        ,[PSCustomObject]@{ Id = '78c5ab69-858a-43ca-a5ac-4ca6f9cdc30d'; Category = 'High Availability'; Control = 'Scalability'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceRemoveQuota'; Message = 'Exceeded quota limit'; State = 'Active'; Version = 5; Description = 'Upgrade your App Service to a Standard plan to avoid request rejects'; LongDescription = 'When an application is part of a shared App Service plan and meets its quota multiple times, incoming requests might be rejected. Your web application can''t accept incoming requests after meeting a quota. To remove the quota, upgrade to a Standard plan.'; Benefits = 'Keep your app healthy'; Tip = ''; LearnMoreLink = 'https://aka.ms/ant-asp'; }
        ,[PSCustomObject]@{ Id = '59a83512-d885-4f09-8e4f-c796c71c686e'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceStandardOrHigher'; Message = 'Site Slots'; State = 'Active'; Version = 7; Description = 'Move your App Service resource to Standard or higher and use deployment slots'; LongDescription = 'When an application is deployed multiple times in a week, problems might occur. You deployed your application multiple times last week. To help you reduce deployment impact to your production web application, move your App Service resource to the Standard (or higher) plan, and use deployment slots.'; Benefits = 'Keep your app healthy while updating'; Tip = ''; LearnMoreLink = 'https://aka.ms/ant-staging'; }
        ,[PSCustomObject]@{ Id = 'dc3edeee-f0ab-44ae-b612-605a0a739612'; Category = 'High Availability'; Control = 'Scalability'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/staticsites'; Key = 'StaticWebAppsUpgradeToStandardSKU'; Message = 'Consider upgrading the hosting plan of the Static Web App(s) in this subscription to Standard SKU.'; State = 'Active'; Version = 7; Description = 'Consider upgrading the hosting plan of the Static Web App(s) in this subscription to Standard SKU.'; LongDescription = 'The combined bandwidth used by all the Free SKU Static Web Apps in this subscription is exceeding the monthly limit of 100GB. Consider upgrading these applications to Standard SKU to avoid throttling.'; Benefits = 'Higher availability for the apps by avoiding throttling.'; Tip = ''; LearnMoreLink = 'https://azure.microsoft.com/pricing/details/app-service/static/'; }
        ,[PSCustomObject]@{ Id = '0dc165fd-69bf-468a-aa04-a69377b6feb0'; Category = 'High Availability'; Control = 'Other'; Impact = 'High'; ServiceName = 'Azure App Service'; ResourceType = 'microsoft.web/sites'; Key = 'AppServiceUseDeploymentSlots'; Message = 'Site Slots'; State = 'Active'; Version = 7; Description = 'Use deployment slots for your App Service resource'; LongDescription = 'When an application is deployed multiple times in a week, problems might occur. You deployed your application multiple times over the last week. To help you manage changes and help reduce deployment impact to your production web application, use deployment slots.'; Benefits = 'Keep your app healthy while updating'; Tip = ''; LearnMoreLink = 'https://aka.ms/ant-staging'; }
        ,[PSCustomObject]@{ Id = 'c8202443-6550-4fc8-9416-5f81089b77d0'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Monitor for SAP Solutions'; ResourceType = 'microsoft.workloads/monitors'; Key = 'UpdateToLatestAMSApiVersion'; Message = 'Upgrade to the latest AMS API version'; State = 'Active'; Version = 2; Description = 'Update to the latest AMS API Version'; LongDescription = 'We have identified calls to a AMS API version which is not recommended. We recommend switching to the latest AMS API version to ensure uninterrupted access to AMS, latest features, and performance improvements.'; Benefits = 'Latest AMS API contain fixes for known issues and additional improvements.'; Tip = 'Upgrade to the latest AMS API version'; LearnMoreLink = 'https://aka.ms/AMSAdvisor'; }
        ,[PSCustomObject]@{ Id = 'eabfd1a1-6092-471b-8cff-22dd044e5535'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Azure Monitor for SAP Solutions'; ResourceType = 'microsoft.workloads/monitors'; Key = 'UpgradeToLatestAMSSdkVersion'; Message = 'Upgrade to the latest Workloads SDK version for AMS'; State = 'Active'; Version = 2; Description = 'Upgrade to the latest Workloads SDK version'; LongDescription = 'Please upgrade to the latest Workloads SDK version to get the best results in terms of model quality, performance and service availability.'; Benefits = 'Latest Workloads SDK contain fixes for known issues and additional improvements.'; Tip = ''; LearnMoreLink = 'https://aka.ms/AMSAdvisor'; }
        ,[PSCustomObject]@{ Id = '2cc17306-822e-45b1-8d7f-5b0d2f2cccdb'; Category = 'Performance'; Control = ''; Impact = 'High'; ServiceName = 'Windows Virtual Desktop'; ResourceType = 'microsoft.desktopvirtualization/hostpools'; Key = 'ChangeMaxSessionLimitForDepthFirstHostPool'; Message = 'Change max session limit for depth first load balanced host pool'; State = 'Active'; Version = 1; Description = 'Change the max session limit for your depth first load balanced host pool to improve VM performance '; LongDescription = 'Depth first load balancing uses the max session limit to determine the maximum number of users that can have concurrent sessions on a single session host. If the max session limit is too high, all user sessions will be directed to the same session host and this may cause performance and reliability issues. Therefore, when setting a host pool to have depth first load balancing, you should also set an appropriate max session limit according to the configuration of your deployment and capacity of your VMs. To fix this, open your host pool''s properties and change the value next to the "Max session limit" setting.'; Benefits = 'Ensure session host functional stability, reliability, and performance when using Windows Virtual Desktop service'; Tip = 'Change the max session limit for your depth first load balanced host pool to improve VM performance.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-desktop/configure-host-pool-load-balancing'; }
        ,[PSCustomObject]@{ Id = '87269ca9-dda6-448e-97ac-c5888b2a2d61'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Windows Virtual Desktop'; ResourceType = 'microsoft.desktopvirtualization/hostpools'; Key = 'ProductionEnvHostPools'; Message = 'Deploy Host Pool to production (non-validation) environment'; State = 'Disabled'; Version = 2.1; Description = 'Not enough production environments enabled'; LongDescription = 'We have determined that too many of your host pools have Validation Environment enabled. In order for Validation Environments to best serve their purpose, you should have at least one, but never more than half of your host pools in Validation Environment. By having a healthy balance between your host pools with Validation Environment enabled and those with it disabled, you will best be able to utilize the benefits of the multistage deployments that Windows Virtual Desktop offers with certain updates. To fix this issue, open your host pool''s properties and select "No" next to the "Validation Environment" setting.'; Benefits = 'Ensure functional stability and business continuity using Windows Virtual Desktop service'; Tip = 'Use Host Pool deployed to production (non-validation) environment to ensure business functionality continuation with increased stability'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-desktop/create-host-pools-powershell'; }
        ,[PSCustomObject]@{ Id = 'd89829c9-dadf-4ddc-87d6-fd746debd5d3'; Category = 'Performance'; Control = ''; Impact = 'Medium'; ServiceName = 'Windows Virtual Desktop'; ResourceType = 'microsoft.desktopvirtualization/hostpools'; Key = 'RegionProximityHostPools'; Message = 'Deploy VMs to different region'; State = 'Active'; Version = 2; Description = 'Improve user experience and connectivity by deploying VMs closer to user''s location.'; LongDescription = 'We have determined that your VMs are located in a region different or far from where your users are connecting from, using Windows Virtual Desktop (WVD). This may lead to prolonged connection response times and will impact overall user experience on WVD. When creating VMs for your host pools, you should attempt to use a region closer to the user. Having close proximity ensures continuing satisfaction with the WVD service and a better overall quality of experience.'; Benefits = 'Improves satisfaction with network round-trip time of the WVD service deployments.'; Tip = 'Use VMs from a region where users are located.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-desktop/connection-latency'; }
        ,[PSCustomObject]@{ Id = '53e0a3cb-3569-474a-8d7b-7fd06a8ec227'; Category = 'High Availability'; Control = 'Other'; Impact = 'Medium'; ServiceName = 'Windows Virtual Desktop'; ResourceType = 'microsoft.compute/virtualmachines'; Key = 'SessionHostNeedsAssistanceForUrlCheck'; Message = 'Provide access to mandatory URLs missing for your Azure Virtual Desktop environment'; State = 'Active'; Version = 8; Description = 'Provide access to mandatory URLs missing for your Azure Virtual Desktop environment'; LongDescription = 'For a session host to deploy and register to Windows Virtual Desktop (WVD) properly, you need a set of URLs in the ''allowed list'' in case your VM runs in a restricted environment. For specific URLs missing from your allowed list, search your application event log for event 3702.'; Benefits = 'Ensure successful deployment and session host functionality when using Windows Virtual Desktop service'; Tip = 'Unblock all URLs from the Safe URL list to ensure a successful deployment of your VMs.'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-desktop/safe-url-list'; }
        ,[PSCustomObject]@{ Id = 'ba1f4576-9ace-4fa9-b0d6-311ad9f2f233'; Category = 'Operational Excellence'; Control = ''; Impact = 'Medium'; ServiceName = 'Windows Virtual Desktop'; ResourceType = 'microsoft.desktopvirtualization/hostpools'; Key = 'ValidationEnvHostPools'; Message = 'Deploy Host Pool to validation environment'; State = 'Disabled'; Version = 2.1; Description = 'No validation environment enabled'; LongDescription = 'We have determined that you do not have a validation environment enabled in current subscription. When creating your host pools, you have selected "No" for "Validation environment" in the properties tab. Having at least one host pool with a validation environment enabled ensures the business continuity through Windows Virtual Desktop service deployments with early detection of potential issues.'; Benefits = 'Ensure business continuity through WVD service deployments'; Tip = 'Use Host Pool deployed to Validation Environment to ensure deployment resilience and business functionality continuation'; LearnMoreLink = 'https://docs.microsoft.com/azure/virtual-desktop/create-validation-host-pool'; }
    )
